// Main assembly
module border_tool() {
    union() {
        // Handle with ergonomic grip
        translate([0, 0, handle_length/2]) {
            color(color_handle) handle();
        }

        // Blade assembly
        translate([0, 0, handle_length + blade_length/2]) {
            color(color_blade) blade_assembly();
        }

        // Adjustable fence
        translate([0, -fence_length/2 - 5, handle_length + 5]) {
            color(color_fence) fence();
        }

        // Depth stop mechanism
        translate([blade_width/2 + 3, 0, handle_length + blade_length - 8]) {
            color(color_blade) depth_stop();
        }
    }
}

// Ergonomic handle with grip ridges
module handle() {
    difference() {
        // Main handle cylinder
        cylinder(h=handle_length, d=handle_diameter, center=true);

        // Grip texture - concentric rings
        for(i = [-handle_length/2 + 5 : 3 : handle_length/2 - 5]) {
            translate([0, 0, i]) {
                cylinder(h=1, d=handle_diameter + 1, center=true);
            }
        }

        // Through hole for hanging
        rotate([90, 0, 0]) {
            cylinder(h=handle_diameter + 2, d=3, center=true);
        }
