}

// Blade assembly with mounting plate
module blade_assembly() {
    union() {
        // Blade body
        difference() {
            cube([blade_width, 3, blade_length], center=true);

            // Cutting edge angle
            translate([0, 1.5, blade_length/2 - 5]) {
                rotate([0, 45, 0]) {
                    cube([blade_width + 2, 3, 10], center=true);
                }
            }

            // Screw holes
            for(pos = [-10, 10]) {
                translate([0, 0, pos]) {
                    cylinder(h=4, d=2.5, center=true);
                }
            }
        }

        // Mounting plate
        translate([0, -2, 0]) {
            cube([blade_width + 6, 1, blade_length - 5], center=true);
        }

        // Screws
        for(pos = [-10, 10]) {
            translate([0, -2.5, pos]) {
                color(color_screws) screw();
            }
        }
    }
}

// Adjustable fence with thumb screws
module fence() {
    difference() {
        // Main fence body
        union() {
            // Fence plate
            cube([5, fence_length, fence_height], center=true);

            // Fence support
            translate([0, 0, -fence_height/2 - 2.5]) {
                cube([8, 5, 5], center=true);
            }

            // Thumb screw knobs
            for(pos = [-15, 15]) {
                translate([-fence_length/2 - 3, pos, 0]) {
                    color(color_screws) cylinder(h=6, d=8, center=true);
                }
