// Main colors
color_handle = "#8B4513";
color_grip = "#1C1C1C";
color_metal = "#C0C0C0";
color_rubber = "#2F2F2F";

// Overall dimensions
handle_length = 120;
handle_diameter = 25;
head_width = 80;
head_length = 100;
head_thickness = 15;

// Handle grip pattern module
module grip_pattern(diameter, length) {
    for (i = [0:4:length-6]) {
        color(color_grip) {
            translate([0, 0, i]) 
                rotate([0, 90, 0])
                    cylinder(h=diameter+2, d=4, center=true);
        }
    }
}

// Handle with ergonomic grip
module handle() {
    color(color_handle) {
        // Main handle body
        cylinder(h=handle_length, d=handle_diameter, center=false);

        // Tapered end
        translate([0, 0, handle_length])
            cylinder(h=10, d1=handle_diameter, d2=handle_diameter-5);
    }

    // Rubber grip rings
    grip_pattern(handle_diameter, handle_length-20);

    // End cap
    color(color_rubber) {
        translate([0, 0, handle_length+10])
            cylinder(h=5, d=handle_diameter+2);
    }
}

// Ridge lifting head
module lifting_head() {
    color(color_metal) {
        // Main head plate
        cube([head_width, head_thickness, head_length], center=true);

        // Reinforcement ribs
        for (i = [-30:30:30]) {
            translate([i, 0, head_length/2-5])
                cube([5, head_thickness+4, 10]);
        }

        // Angled lifting edges
        translate([0, head_thickness/2+2, head_length/2-25])
            rotate([30, 0, 0])
                cube([head_width-10, 3, 30]);

        translate([0, -head_thickness/2-2, head_length/2-25])
            rotate([-30, 0, 0])
                cube([head_width-10, 3, 30]);
    }
}

// Handle-to-head connector
module connector() {
    color(color_metal) {
        // Curved neck
        translate([0, 0, handle_length/2])
            rotate([90, 0, 0])
                difference() {
                    cylinder(h=60, d=20);
                    // Hollow center for weight reduction
                    cylinder(h=60, d=16);
                }

        // Reinforcement plate
        translate([0, 0, handle_length/2+25])
            rotate([90, 0, 0])
                difference() {
                    cylinder(h=5, d=30);
                    cylinder(h=5, d=20);
                }
    }
}

// Assembly
translate([0, 0, 0]) {
    handle();
    translate([0, 0, handle_length/2+30])
        rotate([90, 0, 0])
            lifting_head();
    connector();
}

// Holes for screws (for assembly)
color(color_metal) {
    translate([0, 0, handle_length-15])
        rotate([90, 0, 0])
            cylinder(h=30, d=5);

    translate([0, 0, handle_length/2+25])
        rotate([90, 0, 0])
            cylinder(h=30, d=3);
}

// Furrow tool - Realistic style, medium complexity
// A traditional soil furrower for creating planting rows

$fn = 50;

// Main dimensions (mm)
handle_len = 80;
handle_dia = 12;
head_width = 45;
head_height = 25;
blade_thickness = 3;
grip_len = 30;

// Colors
wood_color = [0.7, 0.5, 0.3];
metal_color = [0.5, 0.5, 0.55];
dark_metal = [0.3, 0.3, 0.35];

module handle_grooves() {
    for(i = [0:5:25]) {
        translate([i, 0, 0])
        rotate([0, 90, 0])
        difference() {
            cylinder(h=handle_dia*1.2, d=handle_dia+1, $fn=6);
            cylinder(h=handle_dia*1.2, d=handle_dia-1, $fn=6);
        }
    }
}

module handle() {
    color(wood_color) {
        // Main handle shaft
        translate([handle_len/2, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=handle_len, d=handle_dia);

        // Grip area with grooves
        translate([handle_len - grip_len/2, 0, 0])
        rotate([0, 90, 0])
        difference() {
            cylinder(h=grip_len, d=handle_dia);
            handle_grooves();
        }

        // End cap
        translate([0, 0, 0])
        sphere(d=handle_dia*1.2);
    }
}

module furrow_head() {
    color(metal_color) {
        // Main head body
        translate([0, 0, -head_height/2])
        cube([head_width, blade_thickness*2, head_height], center=true);

        // Reinforcement ridge
        translate([0, 0, head_height/4])
        cube([head_width*0.8, blade_thickness*2.5, head_height/2], center=true);
    }
}

module furrow_blades() {
    color(dark_metal) {
        // Left blade
        translate([-head_width/3, blade_thickness/2, -head_height/2])
        rotate([0, 0, -30])
        cube([head_width/2, blade_thickness, head_height*1.2]);

        // Right blade
        translate([head_width/3, blade_thickness/2, -head_height/2])
        rotate([0, 0, 30])
        cube([head_width/2, blade_thickness, head_height*1.2]);

        // Center point
        translate([0, blade_thickness/2, -head_height*0.7])
        rotate([0, 0, 0])
        cube([blade_thickness*2, blade_thickness, head_height*1.5]);
    }
}

module connection_bracket() {
    color(metal_color) {
        // Bracket body
        translate([-handle_dia/2, 0, 0])
        cube([handle_dia*2, blade_thickness*3, head_height/2]);

        // Mounting holes
        for(x = [-handle_dia/4, handle_dia/4]) {
            translate([x, -blade_thickness*2, 0])
            rotate([90, 0, 0])
            cylinder(h=blade_thickness*6, d=3, $fn=20);
        }
    }
}

// Assembly
translate([handle_len, 0, 0])
handle();

translate([0, 0, 0])
furrow_head();

translate([0, 0, 0])
furrow_blades();

translate([0, 0, head_height/4])
connection_bracket();

// Trench Tool (Entrenching Tool / Folding Shovel)
// Realistic style with medium complexity

$fn = 36;

// Main dimensions
overall_length = 85;
handle_length = 45;
shovel_width = 20;
shovel_height = 15;

// Colors
color_handle = [0.2, 0.15, 0.1];
color_metal = [0.5, 0.5, 0.55];
color_grip = [0.1, 0.1, 0.1];

module handle_grip() {
    difference() {
        // Main grip tube
        color(color_handle)
        cylinder(h = handle_length, r1 = 3, r2 = 2.8);

        // Grip rings
        for (i = [5:8:handle_length-5]) {
            translate([0, 0, i])
            color(color_grip)
            cylinder(h = 2, r = 3.2);
        }

        // Hollow core
        translate([0, 0, -1])
        cylinder(h = handle_length + 2, r = 1.5);
    }
}

module hinge_mechanism() {
    difference() {
        // Main hinge block
        color(color_metal)
        translate([0, 0, -3])
        cube([6, 4, 6], center = true);

        // Pivot hole
        rotate([90, 0, 0])
        cylinder(h = 7, r = 1.5);

        // Lock pin holes
        for (angle = [0, 90]) {
            rotate([0, 0, angle])
            translate([2.5, 0, -1.5])
            cylinder(h = 3, r = 0.8);
        }
    }
}

module shovel_blade() {
    difference() {
        union() {
            // Main blade
            color(color_metal)
            translate([0, 0, shovel_height/2])
            scale([1, 0.3, 1])
            sphere(d = shovel_width);

            // Blade extension
            translate([0, -shovel_width * 0.3, -shovel_height/2])
            cube([shovel_width * 0.8, shovel_width * 0.6, shovel_height], center = true);

            // Reinforcement ribs
            for (x = [-shovel_width/3, 0, shovel_width/3]) {
                translate([x, -shovel_width * 0.1, 0])
                cube([1, shovel_width * 0.4, shovel_height * 0.8], center = true);
            }
        }

        // Blade edge bevel
        translate([0, -shovel_width * 0.6, -shovel_height/2 - 1])
        cube([shovel_width * 1.2, shovel_width * 0.3, 2], center = true);

        // Mounting hole
        translate([0, shovel_width * 0.2, 0])
        rotate([90, 0, 0])
        cylinder(h = shovel_width * 0.4, r = 1.5);
    }
}

module trench_tool() {
    // Handle assembly
    translate([0, 0, handle_length/2])
    handle_grip();

    // Hinge at end of handle
    translate([0, 0, handle_length])
    hinge_mechanism();

    // Shovel blade
    translate([0, -shovel_width * 0.3, handle_length + shovel_height/2])
    rotate([0, 0, 90])
    shovel_blade();

    // Handle end cap
    color(color_metal)
    translate([0, 0, 0.5])
    cylinder(h = 1, r = 3);

    // Lanyard hole
    translate([0, 0, 1])
    rotate([90, 0, 0])
    cylinder(h = 6, r = 1);
}

// Render the complete tool
trench_tool();

// Ditch Tool - Realistic style, medium complexity
// A sturdy trenching/ditch digging tool with handle and blade

// Overall dimensions
tool_length = 80;
handle_length = 60;
blade_width = 25;
blade_height = 30;
handle_diameter = 12;

// Colors
handle_color = "SaddleBrown";
blade_color = "Gray";
grip_color = "DarkRed";

// Main handle
module handle() {
    color(handle_color) {
        // Main handle shaft
        translate([0, 0, handle_length/2])
            cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Handle end cap
        translate([0, 0, handle_length])
            sphere(d=handle_diameter+2, $fn=32);

        // Grips along handle
        for(i = [10:15:handle_length-15]) {
            color(grip_color) {
                translate([0, 0, i])
                    cylinder(h=8, d=handle_diameter+3, $fn=32);
            }
        }
    }
}

// Blade assembly
module blade() {
    color(blade_color) {
        // Main blade body
        translate([0, 0, -blade_height/2])
            cube([blade_width, 15, blade_height], center=true);

        // Blade cutting edge
        translate([0, 7.5, -blade_height-5])
            cube([blade_width, 15, 10], center=true);

        // Reinforcement ribs
        for(i = [-blade_width/3, 0, blade_width/3]) {
            translate([i, 0, -blade_height/2])
                cube([3, 20, blade_height], center=true);
        }

        // Blade tip
        translate([0, 10, -blade_height-10])
            rotate([90, 0, 0])
                cylinder(h=blade_width, d1=2, d2=0, $fn=3);
    }
}

// Handle-to-blade connector
