    union() {
        color(handle_color) {
            // Transition piece
            translate([0, 0, 0])
                cylinder(h=20, d1=handle_diameter, d2=blade_width, $fn=32);

            // Reinforcement collar
            translate([0, 0, 10])
                cylinder(h=5, d=handle_diameter+6, $fn=32);
        }
    }
}

// Assembly
module ditch_tool() {
    union() {
        // Handle
        translate([0, 0, 20])
            handle();

        // Connector
        connector();

        // Blade
        translate([0, 15, -blade_height/2-10])
            blade();

        // Foot rest
        color(blade_color) {
            translate([0, 15, blade_height/2-15])
                cube([blade_width-4, 3, 8], center=true);
        }
    }
}

// Render the tool
rotate([0, -20, 0])
    ditch_tool();

// Moat Tool (realistic style, medium complexity)
// A specialized tool for creating grooves/moats in wood or soft materials

// Overall dimensions: ~80mm long, ~25mm wide, ~20mm high

// Main body module
module moat_tool() {
    color("silver") {
        // Main handle - ergonomic grip
        translate([0, 0, 0]) {
            difference() {
                // Handle body
                hull() {
                    translate([-30, -8, 0]) cylinder(h=15, d=15, $fn=20);
                    translate([30, -8, 0]) cylinder(h=15, d=15, $fn=20);
                }

                // Grip grooves
                for(i = [-25:10:25]) {
                    translate([i, -8, 7]) rotate([90, 0, 0]) 
                        cylinder(h=16, d=3, $fn=16);
                }

                // Center hole for weight reduction
                translate([0, -8, 7.5]) cylinder(h=16, d=8, $fn=20);
            }
        }

        // Tool shaft
        translate([35, 0, 7.5]) {
            rotate([0, 90, 0]) {
                cylinder(h=40, d=12, $fn=20);
            }
        }

        // Cutting head assembly
        translate([65, 0, 7.5]) {
            rotate([0, 90, 0]) {
                difference() {
                    // Main cutting head body
                    cylinder(h=15, d=20, $fn=30);

                    // Cutting blade slot
                    translate([7.5, 0, 0]) rotate([0, 90, 0]) 
                        cube([1, 16, 10], center=true);

                    // Depth adjustment markings
                    for(i = [2:2:12]) {
                        translate([i, 0, -10]) 
                            cube([0.5, 18, 0.5], center=true);
                    }
                }
            }
        }

        // Cutting blade
        color("gray") {
            translate([72.5, 0, 7.5]) {
                rotate([0, 90, 0]) {
                    difference() {
                        // Blade body
                        cube([1, 16, 10], center=true);

                        // Cutting edge profile
                        translate([0, 0, 5]) 
                            cube([2, 2, 2], center=true);
                        translate([0, 0, -5]) 
                            cube([2, 2, 2], center=true);

                        // Blade relief cuts
                        translate([0, 6, 0]) 
                            cube([1.5, 2, 6], center=true);
                        translate([0, -6, 0]) 
                            cube([1.5, 2, 6], center=true);
                    }
                }
            }
        }

        // Depth adjustment mechanism
        color("darkslategray") {
            translate([65, 0, 17.5]) {
                // Thumb wheel
                difference() {
                    cylinder(h=6, d=12, $fn=20);
                    for(i = [0:60:300]) {
                        rotate([0, 0, i]) 
                            translate([5, 0, 3]) 
                                cube([2, 1, 6], center=true);
                    }
                }

                // Adjustment screw
                translate([0, 0, 3]) {
                    rotate([0, 90, 0]) {
                        cylinder(h=10, d=4, $fn=12);
                    }
                }
            }
        }

        // Safety guard
        color("dimgray") {
            translate([55, 0, 7.5]) {
                rotate([0, 90, 0]) {
                    difference() {
                        cylinder(h=10, d=14, $fn=20);
                        cylinder(h=12, d=10, $fn=20);
                    }
                }
            }
        }

        // Handle end cap
        translate([-40, -8, 7.5]) {
            difference() {
                cylinder(h=5, d=16, $fn=20);
                translate([0, 0, 2.5]) 
                    cylinder(h=5, d=10, $fn=20);
            }
        }

        // Hanging loop
        color("silver") {
            translate([-42, -8, 15]) {
                rotate([90, 0, 0]) {
                    difference() {
                        cylinder(h=3, d=8, $fn=20);
                        translate([0, 0, -1.5]) 
                            cylinder(h=5, d=4, $fn=20);
                    }
                }
            }
        }
    }
}

// Assembly
moat_tool();

// Pond tool - realistic style, medium complexity
// A ladle-style pond tool for skimming debris and water transfer
// Overall length ~85mm, bowl diameter ~45mm

// Tool dimensions
handle_length = 65;
handle_diameter = 8;
bowl_radius = 22;
bowl_thickness = 2;
mesh_hole_radius = 3;
mesh_rows = 6;
mesh_cols = 4;

// Colors
handle_color = "SaddleBrown";
bowl_color = "LightGray";
mesh_color = "DarkGray";

// Main assembly
module pond_tool() {
    union() {
        // Handle
        color(handle_color) handle();

        // Bowl
        color(bowl_color) bowl();

        // Mesh/skimming surface
        color(mesh_color) mesh_surface();
    }
}

// Handle with grip texture
module handle() {
    // Main handle shaft
    cylinder(h=handle_length, d=handle_diameter, $fn=32);

    // Grip rings
    for(i = [0:5]) {
        translate([0, 0, i*10+5])
            difference() {
                cylinder(h=2.5, d=handle_diameter+2, $fn=32);
                cylinder(h=2.5, d=handle_diameter, $fn=32);
            }
    }

    // Handle end cap
    translate([0, 0, handle_length])
        cylinder(h=4, d=handle_diameter+4, $fn=32);
}

// Bowl with proper thickness
module bowl() {
    // Position bowl at end of handle
    translate([0, 0, -bowl_radius])
        rotate([0, 90, 0])
            difference() {
                // Outer bowl
                sphere(r=bowl_radius, $fn=64);

                // Inner hollow
                sphere(r=bowl_radius-bowl_thickness, $fn=64);

                // Flatten bottom for printing
                translate([0, 0, -bowl_radius])
                    cube([bowl_radius*2, bowl_radius*2, bowl_radius], center=true);
            }
}

// Mesh/skimming surface
module mesh_surface() {
    translate([0, -bowl_radius+2, 0])
        rotate([0, 90, 0])
            difference() {
                // Mesh base
                cylinder(h=1.5, r=bowl_radius-2, $fn=64);

                // Drainage holes
                for(i = [0:mesh_rows-1]) {
                    for(j = [0:mesh_cols-1]) {
                        translate([
                            -20 + j * 13,
                            -15 + i * 6,
                            -1
                        ])
                            cylinder(h=3, r=mesh_hole_radius, $fn=16);
                    }
                }
            }
}

// Connection between handle and bowl
module connection() {
    // Reinforcement collar
    translate([0, 0, -bowl_radius/2])
        rotate([0, 90, 0])
            cylinder(h=8, d1=handle_diameter+4, d2=handle_diameter+8, $fn=32);
}

// Render the complete tool
pond_tool();
connection();

// Pool Tool - Realistic Style Medium Complexity
// A combination pool skimmer net and brush tool with telescoping handle

// Main dimensions in mm
handle_length = 70;
handle_diameter = 6;
net_width = 40;
net_depth = 35;
brush_width = 40;
brush_height = 15;
grip_length = 15;

// Colors
color_handle = "silver";
color_net = "blue";
color_brush = "black";
color_grip = "red";

// Telescoping handle segments
module handle_segment(length, diameter, wall) {
    difference() {
        cylinder(h=length, d=diameter, $fn=32);
        translate([0, 0, wall]) cylinder(h=length-wall*2, d=diameter-wall*2, $fn=32);
    }
}

// Handle grip with texture
module handle_grip(length, diameter) {
    color(color_grip) {
        union() {
            // Main grip sleeve
            cylinder(h=length, d=diameter+2, $fn=32);

            // Grip rings
            for(i=[0:2:length-2]) {
                translate([0, 0, i]) cylinder(h=1.5, d=diameter+3, $fn=32);
            }
        }
    }
}

// Pool net frame
module net_frame(width, depth) {
    color(color_net, 0.7) {
        difference() {
            // Outer frame
            cube([width, depth, 4], center=true);

            // Inner opening
            cube([width-4, depth-4, 6], center=true);
        }

        // Net mesh simulation
        for(i=[-width/2+2:3:width/2-2]) {
            for(j=[-depth/2+2:3:depth/2-2]) {
                translate([i, j, 0]) cube([0.5, 0.5, 2], center=true);
            }
        }
    }
}

// Pool brush with bristles
module pool_brush(width, height) {
    union() {
        // Brush base
        color(color_brush) {
            cube([width, 10, height], center=true);
        }

        // Bristles
        for(i=[-width/2+2:2:width/2-2]) {
            color("gray") {
                translate([i, 5, -height/2-1]) 
                    cylinder(h=3, d=0.8, $fn=8);
            }
        }

        // Reinforcement ribs
        color(color_brush) {
            for(i=[-width/2+5:10:width/2-5]) {
                translate([i, 0, 0]) cube([1, 10, height-2], center=true);
            }
        }
    }
}

// Tool head combining net and brush
module tool_head() {
    union() {
        // Net assembly
        translate([0, -net_depth/2-5, 0]) {
            // Net frame
            net_frame(net_width, net_depth);

            // Support arms
            color(color_handle) {
                translate([0, net_depth/2+2, 0]) 
                    cube([2, 4, 4], center=true);
                translate([-net_width/2+2, net_depth/2+2, 0]) 
                    cube([2, 4, 4], center=true);
                translate([net_width/2-2, net_depth/2+2, 0]) 
                    cube([2, 4, 4], center=true);
            }
        }

        // Brush assembly
        translate([0, net_depth/2+15, 0]) {
            rotate([90, 0, 0]) pool_brush(brush_width, brush_height);
        }

        // Connection point
        color(color_handle) {
            translate([0, 0, 4]) 
                cylinder(h=8, d=handle_diameter+2, $fn=32);
        }
    }
}

// Main assembly
union() {
    // Handle segments (telescoping)
    color(color_handle) {
        handle_segment(handle_length*0.4, handle_diameter, 1);
        translate([0, 0, handle_length*0.4]) 
            handle_segment(handle_length*0.35, handle_diameter*0.85, 0.8);
        translate([0, 0, handle_length*0.75]) 
            handle_segment(handle_length*0.25, handle_diameter*0.7, 0.6);
    }

    // Grip at end
    translate([0, 0, handle_length-2]) 
        handle_grip(grip_length, handle_diameter);

    // Tool head
    translate([0, 0, 8]) 
        tool_head();
}

// Realistic Tank Tool - Medium Complexity
// 3D printable tank with tool features

// Main dimensions
tank_length = 80;
tank_width = 40;
tank_height = 35;
wall_thickness = 2;

// Colors
color_body = [0.2, 0.2, 0.2]; // Dark gray
color_tracks = [0.1, 0.1, 0.1]; // Black
color_tool = [0.8, 0.8, 0.8]; // Light gray
color_details = [0.9, 0.2, 0.2]; // Red accents

module tank_body() {
    difference() {
        // Main body
        color(color_body)
        cube([tank_length, tank_width, tank_height], center=true);

        // Hollow interior
        translate([0, 0, -wall_thickness/2])
        cube([tank_length-wall_thickness*2, tank_width-wall_thickness*2, tank_height-wall_thickness], center=true);

        // Top hatch opening
        translate([0, 0, tank_height/2])
        cube([20, 15, wall_thickness*2], center=true);
    }
}

module tracks() {
    // Left track
    translate([-tank_length/2 - 8, -tank_width/2 - 5, 0])
    color(color_tracks) {
        difference() {
            cube([16, 8, tank_height*0.8]);
            for(i = [1:4]) {
                translate([2, 2, i * 6])
                cube([12, 4, 2]);
            }
        }
    }

    // Right track (mirrored)
    translate([-tank_length/2 - 8, tank_width/2 - 3, 0])
    color(color_tracks) {
        difference() {
            cube([16, 8, tank_height*0.8]);
            for(i = [1:4]) {
                translate([2, 2, i * 6])
                cube([12, 4, 2]);
            }
        }
    }
}

module turret() {
    translate([10, 0, tank_height/2 + 5])
    color(color_body) {
        // Turret base
        cylinder(h=8, r1=15, r2=15, center=true);

        // Turret top
        translate([0, 0, 4])
        cylinder(h=3, r1=12, r2=12, center=true);

