grip_depth = 3;

// Main module
module trough_tool() {
    // Main trough body
    difference() {
        // Outer shell
        color([0.7, 0.5, 0.3])  // Brown/rust color
        union() {
            // Base trough shape
            rounded_trough(trough_length, trough_width, trough_height, 5);

            // Reinforcement ribs on bottom
            for(i = [-1, 0, 1]) {
                translate([i * 25, 0, -wall_thickness/2]) {
                    cube([8, trough_width - 10, wall_thickness], center=true);
                }
            }
        }

        // Hollow out interior
        translate([0, 0, wall_thickness]) {
            rounded_trough(trough_length - 2*wall_thickness, 
                          trough_width - 2*wall_thickness, 
                          trough_height - wall_thickness, 3);
        }
    }

    // Left handle
    translate([-trough_length/2 - handle_length/2, 0, trough_height/2]) {
        handle();
    }

    // Right handle
    translate([trough_length/2 + handle_length/2, 0, trough_height/2]) {
        handle();
    }

    // Drainage spout
    translate([0, trough_width/2, trough_height/2 - 5]) {
        spout();
    }
}

// Rounded trough shape with fillet
module rounded_trough(l, w, h, radius) {
    hull() {
        // Bottom corners
        translate([-l/2 + radius, -w/2 + radius, 0])
            cylinder(r=radius, h=h, center=true);
        translate([l/2 - radius, -w/2 + radius, 0])
            cylinder(r=radius, h=h, center=true);
        translate([-l/2 + radius, w/2 - radius, 0])
            cylinder(r=radius, h=h, center=true);
        translate([l/2 - radius, w/2 - radius, 0])
            cylinder(r=radius, h=h, center=true);

        // Top corners
        translate([-l/2 + radius, -w/2 + radius, h])
            cylinder(r=radius, h=1, center=true);
        translate([l/2 - radius, -w/2 + radius, h])
            cylinder(r=radius, h=1, center=true);
        translate([-l/2 + radius, w/2 - radius, h])
            cylinder(r=radius, h=1, center=true);
        translate([l/2 - radius, w/2 - radius, h])
            cylinder(r=radius, h=1, center=true);
    }
}

// Handle with grip
module handle() {
    color([0.1, 0.1, 0.1])  // Black rubberized grip
    union() {
        // Main handle shaft
        rotate([90, 0, 0]) {
            cylinder(r=handle_diameter/2, h=handle_length, center=true);
        }

        // Grip rings
        for(i = [0:5]) {
            translate([0, 0, -handle_length/2 + i*5 + 2.5]) {
                difference() {
                    cylinder(r=handle_diameter/2 + 1, h=3, center=true);
                    rotate([90, 0, 0]) {
                        cube([handle_diameter + 3, grip_depth*2, 4], center=true);
                    }
                }
            }
        }
    }
}

// Pouring spout
