    color([0.7, 0.5, 0.3])
    union() {
        // Spout base
        translate([0, 0, 0])
            cube([20, 10, 10], center=true);

        // Tapered spout
        hull() {
            translate([0, 0, 0])
                cube([20, 8, 8], center=true);
            translate([0, 8, 0])
                cube([12, 1, 4], center=true);
        }
    }
}

