module handle() {
    // Central handle shaft
    color("darkslategray")
    translate([0, 0, 0])
        cylinder(h=tool_size - wall_thickness*2, r=handle_diameter/2);

    // Grip rings
    for(i = [-1:1]) {
        translate([0, 0, i * (handle_length/3)])
            color("black")
            difference() {
                cylinder(h=5, r=handle_diameter/2 + grip_depth);
                cylinder(h=5.1, r=handle_diameter/2);
            }
    }
}

module measurement_markings() {
    // Metric markings on three faces
    for(face = [0:2]) {
        rotate([0, face*90, 0])
        translate([tool_size/2 + 0.1, 0, 0])
        for(i = [1:9]) {
            translate([0, -tool_size/2 + i*tool_size/10, -tool_size/2 + 5])
                color("white")
                cube([0.5, 0.5, 5]);

            if(i % 2 == 0) {
                translate([0, -tool_size/2 + i*tool_size/10, -tool_size/2 + 7])
                    color("white")
                    cube([0.5, 3, 0.5]);
            }
        }
    }
}

module corner_reinforcements() {
    // Reinforced corners for durability
    for(x = [-1,1]) {
        for(y = [-1,1]) {
            for(z = [-1,1]) {
                translate([x*(tool_size/2 - 5), y*(tool_size/2 - 5), z*(tool_size/2 - 5)])
                    color("dimgray")
                    sphere(r=5);
            }
        }
    }
}

module center_finder() {
    // Crosshair at center of each face
    for(axis = [[1,0,0], [0,1,0], [0,0,1]]) {
        color("red") {
            translate([axis[0]*(tool_size/2 + 0.1), 0, 0])
                rotate([0, 90, 0])
                    cylinder(h=tool_size, r=0.5);
            translate([0, axis[1]*(tool_size/2 + 0.1), 0])
                rotate([90, 0, 0])
                    cylinder(h=tool_size, r=0.5);
            translate([0, 0, axis[2]*(tool_size/2 + 0.1)])
                cylinder(h=tool_size, r=0.5);
        }
    }
}

// Assembly
union() {
    main_body();
    handle();
    measurement_markings();
    corner_reinforcements();
    center_finder();
}

// Timber Clamp Tool - Medium Complexity Realistic Design
// Overall dimensions: ~80mm length x ~30mm height x ~25mm width

// Main colors
$fa = 2;
$fs = 0.5;

module main_body() {
    color("saddlebrown") {
        // Main bar with rounded edges
        difference() {
            hull() {
                translate([0, 0, 10]) cylinder(h=15, d=20, $fn=32);
                translate([60, 0, 10]) cylinder(h=15, d=20, $fn=32);
            }

            // Hollow center for weight reduction
            translate([30, 0, 17.5]) cube([80, 12, 20], center=true);
        }

        // Handle grip
        translate([0, 0, 17.5]) {
            difference() {
                cylinder(h=25, d=22, $fn=32);
                translate([0, 0, 2]) cylinder(h=20, d=18, $fn=32);
            }
        }

        // Texture rings on handle
        for(i = [1:3]) {
            translate([0, 0, 5 + i*6]) {
                color("darkgoldenrod") 
                rotate([90, 0, 0]) cylinder(h=0.5, d=22.5, $fn=64);
            }
        }
    }
}

module clamp_jaw() {
    color("dimgray") {
        // Fixed jaw
        translate([60, 0, 5]) {
            difference() {
                union() {
                    cube([15, 25, 5], center=true);
                    translate([7.5, 0, 2.5]) cube([15, 22, 5], center=true);
                }
                // Grip teeth
                for(i = [-10:2:10]) {
                    translate([7.5, i, 2.5]) cube([16, 1, 6], center=true);
                }
            }
        }

        // Movable jaw
        translate([30, 0, 5]) {
            difference() {
                union() {
                    cube([15, 25, 5], center=true);
                    translate([-7.5, 0, 2.5]) cube([15, 22, 5], center=true);
                }
                // Grip teeth
                for(i = [-10:2:10]) {
                    translate([-7.5, i, 2.5]) cube([16, 1, 6], center=true);
                }
            }
        }
    }
}

module screw_mechanism() {
    color("silver") {
        // Threaded rod
        translate([45, 0, 17.5]) {
            difference() {
                cylinder(h=35, d=8, $fn=32);
                // Thread grooves
                for(i = [0:2:35]) {
                    rotate([0, 0, i*15]) 
                    translate([0, 4, i]) cube([8.1, 0.5, 1], center=true);
                }
            }
        }

        // Handle wheel
        translate([0, 0, 17.5]) {
            difference() {
                cylinder(h=8, d=20, $fn=24);
                // Spoke holes
                for(i = [0:60:300]) {
                    rotate([0, 0, i]) 
                    translate([7, 0, 4]) cylinder(h=8.1, d=3, $fn=16);
                }
            }

            // Handle grip
            color("darkred")
            rotate([90, 0, 0]) 
            translate([15, 0, 0]) cylinder(h=15, d=8, $fn=16);
        }

        // Nut bracket
        color("silver")
        translate([30, 0, 17.5]) {
            difference() {
                cube([10, 12, 15], center=true);
                translate([0, 0, 7.5]) cylinder(h=15.1, d=9, $fn=32);
            }
        }
    }
}

module guide_rails() {
    color("darkgoldenrod") {
        // Top rail
        translate([45, 12, 17.5]) {
            cube([30, 3, 15], center=true);
        }

        // Bottom rail
        translate([45, -12, 17.5]) {
            cube([30, 3, 15], center=true);
        }

        // Rail supports
        for(pos = [15, 75]) {
            translate([pos, 0, 17.5]) {
                cube([5, 25, 15], center=true);
            }
        }
    }
}

// Assembly
union() {
    main_body();
    clamp_jaw();
    screw_mechanism();
    guide_rails();

    // Measurement scale
    color("black") {
        for(i = [0:5:50]) {
            translate([i, 0, 26]) cube([0.5, 1, 0.5], center=true);
        }
    }
}

// Log Tool - Realistic style
// A traditional splitting wedge for firewood processing

// Overall dimensions
overall_length = 90;
handle_length = 40;
blade_length = 50;

// Wall thickness for 3D printing
wall_thickness = 2;

// Colors
metal_color = [0.7, 0.7, 0.7];
handle_color = [0.3, 0.2, 0.1];

// Main module
module log_tool() {
    // Handle assembly
    translate([0, 0, -handle_length/2]) {
        // Handle grip
        color(handle_color) handle_grip();

        // Metal collar
        translate([0, 0, handle_length/2 - 5]) {
            color(metal_color) cylinder(h=5, r1=8, r2=7, $fn=32);
        }
    }

    // Blade assembly
    translate([0, 0, handle_length/2]) {
        color(metal_color) blade_assembly();
    }
}

// Handle grip with texture
module handle_grip() {
    difference() {
        // Main handle body
        cylinder(h=handle_length, r=6, $fn=32);

        // Grip grooves
        for(i = [0:4]) {
            translate([0, 0, 5 + i*7]) {
                rotate([0, 0, 45]) {
                    cube([12, 1, 3], center=true);
                }
            }
        }
    }
}

// Blade assembly with wedge shape
module blade_assembly() {
    union() {
        // Transition from handle to blade
        cylinder(h=8, r1=7, r2=6, $fn=32);

        // Main blade body
        translate([0, 0, 4]) {
            difference() {
                // Wedge shape
                hull() {
                    translate([0, 0, 0]) cube([12, 6, 2], center=true);
                    translate([0, 0, blade_length-8]) cube([2, 30, 2], center=true);
                }

                // Cutting edge bevel
                translate([0, -15, blade_length-8]) {
                    rotate([0, 45, 0]) {
                        cube([30, 30, 30], center=true);
                    }
                }
                mirror([0, 1, 0]) {
                    translate([0, -15, blade_length-8]) {
                        rotate([0, 45, 0]) {
                            cube([30, 30, 30], center=true);
                        }
                    }
                }
            }
        }

        // Reinforcement ribs
        for(i = [-1:1]) {
            if(i != 0) {
                translate([i*4, 0, blade_length/2]) {
                    rotate([0, 90, 0]) {
                        cylinder(h=blade_length-8, r=0.5, $fn=8);
                    }
                }
            }
        }

        // Striking surface
        translate([0, 0, blade_length-6]) {
            cube([14, 8, 3], center=true);
        }

        // Safety groove
        translate([0, 0, 10]) {
            rotate([90, 0, 0]) {
                cylinder(h=12, r=0.5, center=true, $fn=16);
            }
        }
    }
}

// Generate the tool
log_tool();

// Realistic Medium-Complexity Staff Tool (Quarterstaff)
// Approx 100 mm tall, 3D-printable, no overhangs

// Overall scale factor
scale_factor = 1.0;

// Dimensions in mm
staff_length = 100 * scale_factor;
staff_diameter = 8 * scale_factor;
grip_length = 30 * scale_factor;
grip_diameter = 12 * scale_factor;
ferrule_length = 8 * scale_factor;
ferrule_diameter = 10 * scale_factor;
ring_width = 2 * scale_factor;
ring_gap = 1.5 * scale_factor;
num_rings = 4;

// Main staff body
module staff_body() {
    color("BurlyWood")
    cylinder(h = staff_length, d = staff_diameter, center = true, $fn = 32);
}

// Leather grip with embossed rings
module grip() {
    difference() {
        color("SaddleBrown")
        cylinder(h = grip_length, d = grip_diameter, center = true, $fn = 32);

        // Central hole
        cylinder(h = grip_length + 1, d = staff_diameter, center = true, $fn = 32);

        // Embossed rings
        for (i = [0 : num_rings - 1]) {
            translate([0, 0, -grip_length/2 + (i + 0.5) * (ring_width + ring_gap)])
            cylinder(h = ring_width, d = grip_diameter + 0.5, center = true, $fn = 32);
        }
    }
}

// Metal ferrule at bottom
module ferrule() {
    difference() {
        color("Silver")
        cylinder(h = ferrule_length, d = ferrule_diameter, center = true, $fn = 32);
        cylinder(h = ferrule_length + 1, d = staff_diameter + 0.5, center = true, $fn = 32);
    }
}

// Runes along top half
module runes() {
    color("DimGray")
    for (i = [0 : 5]) {
        rotate([0, 0, i * 30])
        translate([staff_diameter/2 + 0.3, 0, staff_length/4 + i * 5])
        cube([1, 0.5, 2], center = true);
    }
}

// Assembly
union() {
    staff_body();

    // Grip centered on staff
    translate([0, 0, 0])
    grip();

    // Ferrule at bottom
    translate([0, 0, -staff_length/2 + ferrule_length/2])
    ferrule();

    // Runes on upper half
    translate([0, 0, 0])
    runes();
}

// Realistic Baton Tool (Medium Complexity)
// Approx. 80mm total length

// Color definitions
baton_color = "Silver";
handle_color = "Black";
grip_color = "DarkGray";
tip_color = "Gray";

// Main module
module baton() {
    // Handle assembly
    union() {
        // Handle grip
        color(handle_color) handle_grip();

        // Baton shaft
        color(baton_color) translate([0, 0, 25]) baton_shaft();

        // End cap
        color(handle_color) translate([0, 0, 0]) end_cap();
    }
}

// Handle grip with textured pattern
module handle_grip() {
    difference() {
        // Main handle cylinder
        cylinder(h=25, r1=6, r2=5.5, $fn=32);

        // Grip rings - removed to create texture
        for(i = [1:2:23]) {
            translate([0, 0, i]) 
                cylinder(h=1.5, r1=5.8, r2=5.8, $fn=32);
        }
    }

    // Raised grip bumps
    for(i = [5:5:20]) {
        rotate([0, 0, 0]) 
            translate([5, 0, i]) 
                sphere(r=0.8, $fn=8);
        rotate([0, 0, 120]) 
            translate([5, 0, i]) 
                sphere(r=0.8, $fn=8);
        rotate([0, 0, 240]) 
            translate([5, 0, i]) 
                sphere(r=0.8, $fn=8);
    }
}

// Baton shaft with tapered end
module baton_shaft() {
    union() {
        // Main shaft
        cylinder(h=50, r1=5.5, r2=4, $fn=32);

        // Reinforcement rings
        translate([0, 0, 45]) 
            cylinder(h=3, r1=4.2, r2=4.2, $fn=32);

        // Tip detail
        color(tip_color) translate([0, 0, 50]) baton_tip();
    }
}

// Baton tip with rounded end
module baton_tip() {
    // Cylindrical tip base
    cylinder(h=5, r1=4, r2=3.5, $fn=32);

    // Rounded tip
    translate([0, 0, 5]) 
        sphere(r=3.5, $fn=32);
}

// End cap with lanyard hole
module end_cap() {
    difference() {
        // Main cap
        cylinder(h=5, r1=6, r2=6, $fn=32);

        // Chamfer bottom edge
        translate([0, 0, -0.1]) 
            cylinder(h=2, r1=6.5, r2=6, $fn=32);

        // Lanyard hole
        rotate([90, 0, 0]) 
            cylinder(h=12, r1=1.5, r2=1.5, $fn=16);
    }

    // Small detail ring
    translate([0, 0, 2.5]) 
        cylinder(h=0.5, r1=6.2, r2=6.2, $fn=32);
}

// Generate the baton
baton();

// Realistic Cane Tool - Medium Complexity
// Includes handle, shaft, rubber tip, and decorative collar

// Parameters
handle_length = 25;
handle_thickness = 12;
shaft_length = 60;
shaft_diameter = 8;
tip_diameter = 12;
tip_height = 10;
collar_height = 8;
collar_diameter = 12;

// Main cane assembly
union() {
    // Handle - curved ergonomic grip
    translate([0, 0, shaft_length + handle_thickness/2]) {
        difference() {
            // Main handle body
            color([0.6, 0.3, 0.1]) {
                rotate([0, 90, 0]) {
                    cylinder(h = handle_length, d = handle_thickness, $fn = 32);
                }
            }

            // Finger grooves on underside
            for(i = [0:3]) {
                translate([0, -handle_thickness/2 + 2 + i*5, 0]) {
                    scale([1, 0.3, 0.3]) {
                        sphere(4, $fn = 16);
                    }
                }
            }
        }

        // Handle end cap
        translate([-handle_length/2, 0, 0]) {
            color([0.4, 0.2, 0.1]) {
                sphere(handle_thickness/2, $fn = 32);
            }
        }
    }

    // Shaft - main body
    color([0.5, 0.25, 0.1]) {
        cylinder(h = shaft_length, d = shaft_diameter, $fn = 32);
    }

    // Decorative collar near handle
    translate([0, 0, shaft_length - collar_height]) {
        color([0.7, 0.7, 0.6]) {
            difference() {
                cylinder(h = collar_height, d = collar_diameter, $fn = 32);
                cylinder(h = collar_height + 0.2, d = shaft_diameter + 1, $fn = 32);
            }

            // Ornamental ridges
            for(i = [0:2]) {
                translate([0, 0, i * (collar_height/3)]) {
                    cylinder(h = 1, d = collar_diameter - 1, $fn = 32);
                }
            }
        }
    }

    // Rubber tip - slightly domed
    translate([0, 0, -tip_height/2]) {
        color([0.1, 0.1, 0.1]) {
            difference() {
                cylinder(h = tip_height, d = tip_diameter, $fn = 32);
                translate([0, 0, tip_height - 1]) {
                    sphere(tip_diameter/2, $fn = 32);
                }
            }

            // Traction rings
            for(i = [1:3]) {
                translate([0, 0, i * 2]) {
                    cylinder(h = 0.5, d = tip_diameter - 1, $fn = 32);
                }
            }
        }
    }

    // Shaft texture - subtle wood grain
    difference() {
        cylinder(h = shaft_length, d = shaft_diameter + 0.5, $fn = 32);
        for(i = [0:5:shaft_length]) {
            rotate([0, 0, i * 5]) {
                translate([0, shaft_diameter/2 - 0.2, i]) {
                    cube([0.2, 0.4, 2], center = true);
                }
            }
        }
    }
}

// Support base for 3D printing
%translate([0, 0, -tip_height - 2]) {
    cylinder(h = 2, d = tip_diameter + 4, $fn = 32);
}

// Crop Tool - Garden Pruning Shears
// Medium complexity realistic style

// Constants
handle_length = 45;
handle_diameter = 12;
blade_length = 70;
blade_width = 20;
blade_thickness = 3;
pivot_radius = 5;
safety_lock_width = 8;

// Main assembly
module pruning_shears() {
    color("silver") {
        union() {
            // Left handle
            translate([-blade_length/2 - 10, 0, 0]) 
                rotate([0, 0, -15]) 
                handle_assembly();

            // Left blade
            translate([0, 0, 0]) 
                rotate([0, 0, -15]) 
                blade_assembly(true);

            // Right handle
            translate([-blade_length/2 - 10, 0, 0]) 
                rotate([0, 0, 15]) 
                mirror([1, 0, 0]) 
                handle_assembly();

            // Right blade
            translate([0, 0, 0]) 
                rotate([0, 0, 15]) 
                mirror([1, 0, 0]) 
                blade_assembly(false);

            // Pivot bolt
            translate([0, 0, 0]) 
                pivot_mechanism();

            // Safety lock
            translate([0, -handle_diameter/2 - 2, 0]) 
                rotate([90, 0, 0]) 
                safety_lock();
        }
    }
}

// Handle assembly with grip
module handle_assembly() {
    union() {
        // Main handle tube
        translate([0, 0, -handle_length/2]) 
            cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Ergonomic grip
        for(i = [0:3]) {
            translate([0, 0, -handle_length/2 + i*10 + 5]) 
                rotate([0, 0, 45]) 
                cube([handle_diameter*1.2, handle_diameter*1.2, 8], center=true);
        }

        // End cap
        translate([0, 0, handle_length/2]) 
            cylinder(h=5, d=handle_diameter*1.2, $fn=32);

        // Hanging hole
        translate([0, 0, handle_length/2 - 2.5]) 
            difference() {
                cylinder(h=5, d=handle_diameter*1.2, $fn=32);
                translate([0, 0, -1]) 
                    cylinder(h=7, d=5, $fn=32);
            }
    }
}

// Blade assembly
module blade_assembly(is_left) {
    union() {
        // Blade base
        translate([blade_length/2, 0, 0]) 
            cube([blade_length, blade_width, blade_thickness], center=true);

        // Curved cutting edge
        difference() {
            translate([blade_length/2, 0, 0]) 
                cube([blade_length, blade_width, blade_thickness], center=true);

            // Cutting bevel
            translate([blade_length*0.75, 0, blade_thickness/2]) 
                rotate([0, 15, 0]) 
                cube([blade_length*1.5, blade_width*2, blade_thickness*2], center=true);

            // Serrated edge
            for(i = [0:10]) {
                translate([i*6, blade_width/2 - 1, 0]) 
                    rotate([0, 0, -30]) 
                    cube([2, 3, blade_thickness*1.1], center=true);
            }
        }

        // Blade reinforcement
        translate([blade_length*0.3, 0, blade_thickness/2 + 1]) 
            cube([blade_length*0.4, blade_width*0.6, 2], center=true);

        // Pivot hole
        translate([0, 0, 0]) 
            cylinder(h=blade_thickness*2, d=pivot_radius*2, center=true, $fn=32);
    }
}

// Pivot mechanism
module pivot_mechanism() {
    union() {
        // Main bolt
        cylinder(h=blade_thickness*2 + 4, d=pivot_radius*2, center=true, $fn=32);

        // Bolt head
        translate([0, 0, blade_thickness + 2]) 
            cylinder(h=3, d=pivot_radius*3, $fn=6);

        // Nut
        translate([0, 0, -blade_thickness - 2]) 
            difference() {
                cylinder(h=3, d=pivot_radius*3, $fn=6);
                cylinder(h=5, d=pivot_radius, center=true, $fn=32);
            }
    }
}

// Safety lock mechanism
module safety_lock() {
    color("red") {
        difference() {
            // Lock slider
            cube([safety_lock_width, safety_lock_width, 15], center=true);

            // Grip ridges
            for(i = [0:2]) {
                translate([-safety_lock_width/2 + i*3, 0, 0]) 
                    cube([0.5, safety_lock_width + 2, 14], center=true);
            }

            // Mounting hole
            translate([0, 0, -8]) 
                cylinder(h=20, d=4, $fn=32);
        }
    }
}

// Render the shears
pruning_shears();

// Realistic whip with medium complexity
// 3D printable whip tool ~90mm long

// Handle segments
module handle_segment(length, radius, grip_depth) {
    // Main cylinder
    cylinder(h=length, r=radius, $fn=32);

    // Grip rings
    for(i=[1:grip_depth:length-5]) {
        translate([0,0,i]) 
            cylinder(h=2, r=radius+0.5, $fn=32);
    }
}

// Handle tip with loop
module handle_tip() {
    difference() {
        union() {
            // Tapered end
            cylinder(h=8, r1=4, r2=2, $fn=32);

            // Loop for hanging
            translate([0,0,8])
                rotate([90,0,0])
                    torus(2, 1, 16);
        }

        // Remove loop hole
        translate([0,0,8])
            rotate([90,0,0])
                cylinder(h=5, r=1, $fn=16);
    }
}

// Torus helper
module torus(r_major, r_minor, fn) {
    rotate_extrude($fn=fn) {
        translate([r_major, 0, 0])
            circle(r=r_minor, $fn=fn);
    }
}

// Whip thong segments
module thong_segment(length, start_width, end_width, thickness) {
    hull() {
        translate([0,0,0])
            scale([start_width, thickness, 1])
                sphere(r=1, $fn=16);

        translate([0,0,length])
            scale([end_width, thickness, 1])
                sphere(r=1, $fn=16);
    }
}

// Cracker end
module cracker() {
    // Tapered cracker
    difference() {
        union() {
            // Main cracker
            translate([0,0,0])
                thong_segment(15, 1.5, 0.5, 0.3);

            // Knot detail
            translate([0,0,15])
                sphere(r=0.7, $fn=16);
        }

        // Split ends
        translate([0,0,15])
            rotate([0,90,0])
                cube([10,0.2,0.2], center=true);
    }
}

// Handle assembly
module handle() {
    color([0.4, 0.2, 0.1]) {
        union() {
            // Main handle
            handle_segment(40, 6, 5);

            // End cap
            translate([0,0,40])
                handle_tip();

            // Ferrule
            translate([0,0,-2])
                cylinder(h=3, r=7, $fn=32);
        }
    }
}

// Thong assembly
module thong() {
    color([0.3, 0.15, 0.05]) {
        union() {
            // First segment
            translate([0,0,-2])
                thong_segment(20, 4, 3, 1.2);

            // Second segment
            translate([0,0,-22])
                thong_segment(15, 3, 2, 0.8);

            // Third segment
            translate([0,0,-37])
                thong_segment(12, 2, 1.5, 0.5);

            // Fourth segment
