module handle() {
    color(WOOD)
    union() {
        // Main handle
        cylinder(h = flail_length * 0.7, d = handle_diameter, $fn = 16);

        // Handle grip rings
        for(i = [1:4]) {
            translate([0, 0, i * 12])
            cylinder(h = 2, d = handle_diameter + 2, $fn = 16);
        }

        // Handle end cap
        translate([0, 0, -1])
        cylinder(h = 3, d = handle_diameter + 2, $fn = 16);
    }
}

module chain_link() {
    color(METAL)
    rotate([90, 0, 0])
    difference() {
        cylinder(h = chain_link_length, d = chain_link_diameter * 2, $fn = 16);
        translate([0, 0, -1])
        cylinder(h = chain_link_length + 2, d = chain_link_diameter * 1.2, $fn = 16);
    }
}

module chain() {
    links = 4;
    for(i = [0:links-1]) {
        translate([0, 0, -i * chain_link_length])
        chain_link();
    }
}

module spiked_ball() {
    color(DARK_METAL)
    union() {
        // Main ball
        sphere(r = spike_ball_radius, $fn = 32);

        // Spikes
        for(i = [0:15]) {
            rotate([i * 45, 0, 0])
            translate([spike_ball_radius, 0, 0])
            rotate([0, 0, 90])
            cylinder(h = 8, d1 = 3, d2 = 1, $fn = 8);
        }

        // Connection ring
        translate([0, 0, spike_ball_radius])
        rotate([90, 0, 0])
        difference() {
            cylinder(h = 4, d = 6, $fn = 16);
            translate([0, 0, -1])
            cylinder(h = 6, d = 3, $fn = 16);
        }
    }
}

// Assembly
union() {
    // Handle
    handle();

    // Chain connection point
    translate([0, 0, flail_length * 0.7])
    rotate([0, 90, 0])
    difference() {
        cylinder(h = 12, d = 6, $fn = 16);
        translate([0, 0, -1])
        cylinder(h = 14, d = 3, $fn = 16);
    }

    // Chain
    translate([0, 6, flail_length * 0.7])
    chain();

    // Spiked ball
    translate([0, 6, flail_length * 0.7 - (4 * chain_link_length) - spike_ball_radius])
    spiked_ball();
}

