// Helper: rounded box for head
module rounded_box(size, r) {
    x=size.x-2*r; y=size.y-2*r; z=size.z-2*r;
    hull() {
        for(dx=[-1,1], dy=[-1,1], dz=[-1,1])
            translate([dx*r, dy*r, dz*r])
                sphere(r=r, $fn=16);
