    offset_y=(rows-1)*spacing_y/2;

    for(i=[0:cols-1], j=[0:rows-1]) {
        translate([i*spacing_x-offset_x, j*spacing_y-offset_y, 20.5])
            cylinder(h=1.5, d=3, $fn=16);
