module windlass_assembly() {
    union() {
        // Wheel with ratchet teeth
        translate([0, 0, shaft_l/2]) rotate([90, 0, 0])
            wheel();

        // Handle on wheel
        translate([wheel_d/2 + handle_l/2, 0, shaft_l/2]) 
            rotate([90, 0, 0]) 
                handle();

        // Central shaft
        translate([0, 0, shaft_l/2]) 
            rotate([90, 0, 0]) 
                shaft();

        // Mounting base
        translate([0, 0, 0]) 
            base();

