            handle();
            translate([20,0,0]) rotate([0,0,0]) {
                jaw_half(-1);
            }
        }
    }

    // Pivot pin at center
    translate([20,0,3]) pivot_pin();

    // Safety lock on first handle
    translate([35,6,2.5]) rotate([0,0,open_angle]) safety_lock();
