crossbeam_diameter = 3;
platform_height = 8;

// Color definitions
color_structure = [0.7, 0.7, 0.7]; // Steel gray
color_platform = [0.5, 0.5, 0.5]; // Darker gray
color_details = [0.8, 0.6, 0.2]; // Brass/yellow
color_pipe = [0.9, 0.9, 0.9]; // Light gray

module derrick_leg(height, angle = 0) {
    rotate([0, angle, 0])
        cylinder(h = height, d = leg_diameter, center = true);
}

module crossbeam(length, rotation = 0) {
    rotate([0, 0, rotation])
        cylinder(h = length, d = crossbeam_diameter, center = true);
}

module support_base() {
    color(color_structure)
    difference() {
        cube([base_size, base_size, platform_height], center = true);
        translate([0, 0, platform_height/2])
            cube([base_size-4, base_size-4, platform_height+1], center = true);
    }
}

module drilling_platform() {
    color(color_platform)
    translate([0, 0, tower_height/2 - platform_height/2])
    difference() {
        cube([base_size-5, base_size-5, platform_height], center = true);
        translate([0, 0, platform_height/2])
            cube([base_size-15, base_size-15, platform_height+1], center = true);
    }
}

module derrick_tower() {
    color(color_structure)
    union() {
        // Four legs
        for(i = [0:3]) {
            rotate([0, 0, i*90])
            translate([base_size/2-2, base_size/2-2, 0])
                derrick_leg(tower_height, atan((base_size-4)/tower_height));
        }

        // Horizontal crossbeams
        for(h = [platform_height+5:15:tower_height-15]) {
            for(i = [0:1]) {
                rotate([0, 0, i*90])
                translate([0, 0, h-tower_height/2])
                    crossbeam(base_size-4);
            }
        }

        // Diagonal supports
        for(h = [platform_height+10:15:tower_height-15]) {
            rotate([0, 0, 45])
            translate([0, 0, h-tower_height/2])
                crossbeam((base_size-4)*sqrt(2));
        }
    }
}

module crown_block() {
    color(color_structure)
    translate([0, 0, tower_height/2+5])
    union() {
        // Crown block housing
        cube([base_size-10, 6, 8], center = true);

        // Sheaves
        color(color_details)
        for(i = [-1, 1]) {
            translate([i*8, 0, 0])
                cylinder(h = 3, d = 5, center = true);
            translate([i*8, 0, 0])
                rotate([90, 0, 0])
                    cylinder(h = 3.1, d = 1, center = true);
        }
    }
}

module drill_pipe() {
    color(color_pipe)
    translate([0, 0, -tower_height/2+2])
    cylinder(h = tower_height-10, d = 3, center = false);
}

module catwalk() {
    color(color_platform)
    translate([base_size/2+2, 0, -tower_height/2+platform_height/2])
    rotate([0, 0, 90])
    cube([tower_height/2, 8, platform_height/2], center = true);
}

module ladder() {
    color(color_structure)
    translate([0, base_size/2-1, 0])
    union() {
        // Side rails
        for(x = [-2, 2]) {
            translate([x, 0, 0])
                cube([1, 2, tower_height-10], center = true);
        }

        // Rungs
        for(h = [-tower_height/2+5:5:tower_height/2-5]) {
            translate([0, 0, h])
                cube([5, 2, 1], center = true);
        }
    }
}

// Assemble the complete derrick
union() {
    support_base();
    derrick_tower();
    drilling_platform();
    crown_block();
    drill_pipe();
    catwalk();
    ladder();
}

