// Hook tip with barb
module hook_tip() {
    color(hook_color)
    union() {
        // Main hook curve
        difference() {
            // Outer curve
            rotate([90, 0, 0])
                cylinder(h=hook_length, d=hook_thickness*2, $fn=32);

            // Inner hollow for curve
            translate([0, 0, -1])
                rotate([90, 0, 0])
                    cylinder(h=hook_length+2, d=hook_thickness*1.4, $fn=32);
        }

        // Barb
        translate([hook_thickness, hook_length-barb_length-2, 0])
            rotate([0, 90, 0])
                cube([barb_length, barb_thickness, hook_thickness*1.5]);
    }
}

// Reinforcement collar
module collar() {
    color(hook_color)
    cylinder(h=8, d=handle_diameter+4, $fn=32);
}

