shaft_diameter = 12;
grip_diameter = 22;
grip_length = 35;

// Tip dimensions
fork_width = 8;
fork_thickness = 3;
fork_gap = 5;
fork_length = 15;

// Handle grip pattern
grip_ridges = 12;
ridge_height = 0.5;

// Quality settings
$fn = 32;

// Main tool assembly
module axle_tool() {
    union() {
        // Handle assembly
        color([0.2, 0.2, 0.2]) {
            handle();
        }

        // Shaft
        color([0.7, 0.7, 0.7]) {
            shaft();
        }

        // Fork tip
        color([0.8, 0.8, 0.8]) {
            fork_tip();
        }
    }
}

// Handle with grip pattern
module handle() {
    // Main handle cylinder
    cylinder(h=handle_length, d=grip_diameter, center=false);

    // Grip ridges
    for(i = [0:grip_ridges-1]) {
        translate([0, 0, i * (grip_length / grip_ridges)]) {
            difference() {
                cylinder(h=grip_length / grip_ridges + 1, d=grip_diameter + ridge_height * 2, center=false);
                cylinder(h=grip_length / grip_ridges + 2, d=grip_diameter, center=false);
            }
        }
    }
}

// Main shaft
module shaft() {
    translate([0, 0, handle_length]) {
        // Tapered section
        cylinder(h=5, d1=grip_diameter, d2=shaft_diameter, center=false);

        // Main shaft
        translate([0, 0, 5]) {
            cylinder(h=shaft_length - 5 - fork_length, d=shaft_diameter, center=false);
        }
    }
}

// Fork tip for axle removal
module fork_tip() {
    translate([0, 0, handle_length + (shaft_length - fork_length)]) {
        // Main shaft continues
        cylinder(h=fork_length, d=shaft_diameter, center=false);

        // Remove center to create fork
        translate([0, 0, -0.1]) {
            cube([fork_gap, fork_thickness + 0.2, fork_length + 0.2], center=true);
        }

        // Add chamfer to fork tips
        translate([0, 0, handle_length + (shaft_length - fork_length) + fork_length]) {
            cylinder(h=2, d1=shaft_diameter, d2=shaft_diameter - 2, center=false);
        }

        // Strengthening ribs
        for(side = [-1, 1]) {
            translate([side * (fork_width/2 - fork_thickness/2), 0, 0]) {
                cube([fork_thickness, fork_thickness, fork_length], center=true);
            }
        }
    }
}

// Alignment marks
module alignment_marks() {
    color([0.1, 0.1, 0.1]) {
        // Center line indicator
        translate([0, grip_diameter/2 + 0.1, handle_length/2]) {
            rotate([90, 0, 0]) {
                cylinder(h=handle_length * 0.8, d=1, center=true);
            }
        }

        // Measurement marks
        for(i = [0:4]) {
            translate([grip_diameter/2 + 0.1, 0, handle_length - i * 10]) {
                cube([2, 1, 0.5], center=true);
            }
        }
    }
}

// Generate the tool
axle_tool();
alignment_marks();

