// Handle with ergonomic ridges
module handle() {
    color(handle_color)
    union() {
        // Main cylinder
        cylinder(h = handle_length, d = handle_diameter, $fn = 60);

        // Grip rings
        for (i = [0 : grip_ring_gap : handle_length - grip_ring_height]) {
            translate([0, 0, i])
                cylinder(h = grip_ring_height, d1 = handle_diameter + 2, d2 = handle_diameter + 1, $fn = 60);
        }

        // End cap
        translate([0, 0, handle_length])
            cylinder(h = 3, d1 = handle_diameter, d2 = handle_diameter + 4, $fn = 60);
    }
}

// Blade with protractor markings
module blade() {
    color(blade_color)
    union() {
        // Main blade body
        translate([0, 0, -blade_thickness / 2])
            cube([blade_length, blade_width, blade_thickness], center = true);

        // Strengthening spine
        translate([0, 0, -blade_thickness / 2])
            cube([blade_length, 4, blade_thickness + 1], center = true);

        // Protractor arc
        translate([0, -blade_width / 2 + 2, blade_thickness / 2 + 0.1])
            color("Black")
            linear_extrude(height = protractor_thickness)
                arc(0, 90, protractor_radius, 1.2);

        // Angle marks
        for (angle = [0 : 15 : 90]) {
            rotate([0, 0, angle])
                translate([protractor_radius - 2, 0, blade_thickness / 2 + 0.1])
                    rotate([0, 0, -angle])
                        cube([3, 0.6, protractor_thickness + 0.1]);
        }
    }
}

// Pivot bolt
module pivot_bolt() {
    union() {
        // Bolt head
        color(blade_color)
        translate([0, 0, -bolt_head_diameter / 2])
            cylinder(h = bolt_head_diameter, d = bolt_head_diameter, $fn = 6);

        // Shaft
        color("Gray")
        translate([0, 0, -blade_thickness / 2 - 1])
            cylinder(h = blade_thickness + 2, d = bolt_diameter, $fn = 20);
    }
}

// Helper: arc primitive
module arc(a0, a1, r, w, h = 1) {
    difference() {
        circle(r = r, $fn = 60);
        circle(r = r - w, $fn = 60);
    }
}

