black = [0.1, 0.1, 0.1];
red = [0.8, 0.1, 0.1];
white = [0.9, 0.9, 0.9];

module rafter_square() {
    difference() {
        // Main L-shaped body
        union() {
            // Long arm
            color(aluminum) translate([0, 0, 0])
                cube([length, thickness, width]);

            // Short arm
            color(aluminum) translate([0, 0, 0])
                cube([thickness, width, width]);

            // Reinforcement fillet
            color(aluminum) 
                translate([thickness, 0, 0])
                cube([length-thickness, thickness, thickness]);
        }

        // Scribe holes along long arm
        for(i = [1:12]) {
            translate([i*6 + 5, thickness/2, width/2])
                rotate([90, 0, 0])
                cylinder(h=thickness+1, r=1.5, $fn=16);
        }

        // Common angles cutout
        translate([length-15, thickness/2, width-15])
            rotate([0, 90, 0])
            cube([30, thickness+1, 15]);

        // Branding area recess
        translate([10, thickness/2, 5])
            cube([25, thickness+1, 12]);
    }
}

module measurement_markings() {
    // Long arm markings (inches)
    for(i = [0:12]) {
        if(i % 2 == 0) {
            // Major tick marks
            color(black) translate([5 + i*5, thickness+0.1, 2])
                cube([0.5, 0.2, 8]);
        } else {
            // Minor tick marks
            color(black) translate([5 + i*5, thickness+0.1, 2])
                cube([0.3, 0.2, 5]);
        }
    }

    // Short arm markings (inches)
    for(i = [0:5]) {
        color(black) translate([2, thickness+0.1, 5 + i*5])
            cube([8, 0.2, 0.5]);
    }
}

module grip_pattern() {
    // Diamond knurl pattern on back
    for(x = [10:5:length-10]) {
        for(y = [5:5:width-5]) {
            color(black) translate([x, thickness-0.5, y])
                rotate([0, 90, 45])
                cylinder(h=0.5, r1=1.2, r2=0.8, $fn=4);
        }
    }
}

module angle_markings() {
    // Common rafter angles
    angles = [15, 30, 45];
    for(angle = angles) {
        translate([length-20, thickness/2, 20])
            rotate([0, -angle, 0])
            color(red) cube([15, 0.2, 1]);
    }
}

module text_markings() {
    // Brand text (represented as raised blocks)
    translate([12, thickness+0.2, 8])
        color(white) cube([2, 0.5, 0.5]);

    translate([15, thickness+0.2, 8])
        color(white) cube([2, 0.5, 0.5]);

    translate([18, thickness+0.2, 8])
        color(white) cube([2, 0.5, 0.5]);
}

