// Strut Tool - Realistic Style, Medium Complexity
// A 3D printable strut removal tool for automotive suspension work
// Features angled handle, forked strut engagement, and grip patterns

// Main parameters
handle_length = 80;
handle_diameter = 22;
fork_width = 35;
fork_thickness = 8;
fork_length = 45;
bend_angle = 30;

// Color constants
handle_color = "DarkSlateGray";
fork_color = "Silver";
grip_color = "Black";

module handle_grip() {
    // Create knurled grip pattern
    color(grip_color) {
        difference() {
            cylinder(h=handle_length, d=handle_diameter, $fn=50);

            // Cut knurling grooves
            for(i = [0:5:handle_length-5]) {
                translate([0, 0, i])
                rotate([0, 0, 45])
                linear_extrude(height=3)
                circle(d=handle_diameter-2, $fn=6);
            }
        }
    }
}

module handle_body() {
    color(handle_color) {
        difference() {
            // Main handle cylinder
            cylinder(h=handle_length, d=handle_diameter, $fn=32);

            // Add grip section
            translate([0, 0, 5])
            cylinder(h=handle_length-10, d=handle_diameter-2, $fn=32);

            // End cap recess
            translate([0, 0, handle_length-2])
            cylinder(h=3, d=handle_diameter-6, $fn=32);
        }
    }
}

module fork_prongs() {
    color(fork_color) {
        // Left prong
        translate([-fork_width/4, 0, 0])
        union() {
            // Main prong body
            cube([fork_width/2, fork_thickness, fork_length]);

            // Rounded tip
            translate([fork_width/4, fork_thickness/2, fork_length])
            rotate([90, 0, 0])
            cylinder(h=fork_width/2, d=fork_thickness, $fn=16);

            // Reinforcement rib
            translate([0, -1, fork_length/2])
            cube([fork_width/2, 2, fork_length/4]);
        }

        // Right prong
        translate([fork_width/4, 0, 0])
        mirror([1, 0, 0])
        union() {
            cube([fork_width/2, fork_thickness, fork_length]);

            translate([fork_width/4, fork_thickness/2, fork_length])
            rotate([90, 0, 0])
            cylinder(h=fork_width/2, d=fork_thickness, $fn=16);

            translate([0, -1, fork_length/2])
            cube([fork_width/2, 2, fork_length/4]);
        }
    }
}

module fork_base() {
    color(fork_color) {
        // Transition from handle to fork
        hull() {
            // Handle connection
            translate([0, 0, -5])
            cylinder(h=10, d=handle_diameter, $fn=32);

            // Fork base
            cube([fork_width, fork_thickness, 15], center=true);
        }
    }
}

module strut_tool() {
    // Handle assembly
    translate([0, 0, 0])
    union() {
        handle_body();
        handle_grip();
    }

    // Fork assembly with bend
    translate([0, 0, handle_length])
    rotate([bend_angle, 0, 0])
    translate([0, 0, 5])
    union() {
        fork_base();
        fork_prongs();
    }

    // Add tool markings
    color("White") {
        translate([handle_diameter/2 + 1, 0, handle_length/2])
        rotate([0, 90, 0])
        linear_extrude(height=0.5)
        text("STRUT", size=4, halign="center", valign="center");
    }
}

// Render the complete tool
strut_tool();

