// Optional helper module for rounded edges
module rounded_cube(size, r) {
    translate([r,r,0]) cube([size.x-2*r,size.y-2*r,size.z]);
    translate([r,r,size.z-r]) sphere(r);
    translate([size.x-r,r,size.z-r]) sphere(r);
    translate([r,size.y-r,size.z-r]) sphere(r);
    translate([size.x-r,size.y-r,size.z-r]) sphere(r);
}

// Handle with finger grooves
module drill_handle() {
    color(handle_color)
    difference() {
        cylinder(h = handle_len, d = handle_dia, $fn = 40);
        // Internal hollow for printability / weight
        cylinder(h = handle_len + 2, d = handle_dia - 4, $fn = 40);
        // Finger grooves
        for (i = [1:4]) {
            translate([0, 0, i*handle_len/5])
                rotate([90,0,0])
                    cylinder(h = 4, d = 6, $fn = 20);
        }
    }
}

// Motor body
module drill_body() {
    color(body_color)
    union() {
        // Main cylindrical body
        translate([0, 0, handle_len])
            cylinder(h = body_len, d = body_dia, $fn = 40);
        // Transition cone
        translate([0, 0, handle_len])
            cylinder(h = transition_h, d1 = handle_dia, d2 = body_dia, $fn = 40);
        // Top flat cap
        translate([0, 0, handle_len + body_len])
            cylinder(h = 2, d = body_dia, $fn = 40);
        // Trigger guard
        translate([body_dia/2 - 2, 0, handle_len - 10])
            rotate([0,90,0])
                difference() {
                    cylinder(h = 8, d = 8, $fn = 20);
                    cylinder(h = 9, d = 5, $fn = 20);
                }
    }
}

// Three-jaw chuck
module chuck() {
    color(chuck_color)
    union() {
        // Main chuck cylinder
        translate([0, 0, handle_len + body_len])
            cylinder(h = chuck_len, d = chuck_dia, $fn = 40);
        // Chuck jaws (simplified as three ridges)
        for (a = [0:120:240]) {
            rotate([0,0,a])
                translate([chuck_dia/2 - 2, 0, handle_len + body_len])
                    cube([4, 1.5, chuck_len], center = true);
        }
    }
}

// Drill bit
module bit() {
    color(bit_color)
    translate([0, 0, handle_len + body_len + chuck_len])
        cylinder(h = bit_len, d = bit_dia, $fn = 12);
}

// Assembly
drill_handle();
drill_body();
chuck();
bit();

// Adjustable Wrench - OpenSCAD Support Tool
// Realistic style, medium complexity

// Color definitions
$fn=50;

module handle() {
    color([0.1, 0.1, 0.1]) {
        // Main handle body
        translate([0, 0, 5]) {
            difference() {
                cube([15, 12, 100], center=true);
                // Grip grooves
                for(i=[-40:10:40]) {
                    translate([0, 0, i]) {
                        cube([16, 13, 2], center=true);
                    }
                }
            }
        }

        // Handle end cap
        translate([0, 0, -55]) {
            cylinder(h=10, r=8);
        }

        // Handle grip texture
        for(i=[-35:7:35]) {
            color([0.2, 0.2, 0.2]) {
                translate([0, 7, i]) {
                    cube([15, 1.5, 4], center=true);
                }
            }
        }
    }
}

module jaw_base() {
    color([0.7, 0.7, 0.7]) {
        // Fixed jaw
        translate([-20, -15, 45]) {
            cube([20, 30, 25]);
        }

        // Jaw reinforcement
        translate([-25, -10, 45]) {
            cube([5, 20, 25]);
        }
    }
}

module movable_jaw() {
    color([0.7, 0.7, 0.7]) {
        // Moving jaw
        translate([5, -15, 45]) {
            cube([20, 30, 25]);
        }

        // Jaw guide
        translate([5, -10, 35]) {
            cube([15, 20, 5]);
        }
    }
}

module adjustment_mechanism() {
    color([0.5, 0.5, 0.5]) {
        // Threaded rod
        translate([10, 0, 35]) {
            rotate([90, 0, 0]) {
                cylinder(h=30, r=3);
            }
        }

        // Adjustment knob
        translate([10, 0, 20]) {
            cylinder(h=15, r=8);

            // Knurling on knob
            for(i=[0:20:359]) {
                rotate([0, 0, i]) {
                    translate([7.5, 0, 0]) {
                        cube([1, 0.5, 15], center=true);
                    }
                }
            }
        }
    }
}

module jaw_teeth() {
    color([0.8, 0.8, 0.8]) {
        // Fixed jaw teeth
        for(i=[-10:5:10]) {
            translate([-20, i, 57.5]) {
                cube([3, 2, 5]);
            }
        }

        // Moving jaw teeth
        for(i=[-10:5:10]) {
            translate([22, i, 57.5]) {
                cube([3, 2, 5]);
            }
        }
    }
}

module size_indicator() {
    color([0, 0, 0]) {
        // Measurement marks
        for(i=[0:2:20]) {
            translate([i-10, 0, 30]) {
                cube([0.5, 5, 0.2]);
            }
        }
    }
}

// Assembly
union() {
    handle();
    jaw_base();
    movable_jaw();
    adjustment_mechanism();
    jaw_teeth();
    size_indicator();
}

// Adjustable wrench base tool - realistic style, medium complexity
// Overall dimensions: ~85mm length, 30mm width, 12mm height

// Main color scheme
$fn = 50;

// Main handle body
module handle() {
    color([0.2, 0.2, 0.2]) {
        // Main grip section
        difference() {
            // Handle core
            union() {
                translate([0, 0, 6]) 
                    cube([60, 20, 12], center=true);

                // Rounded ends
                translate([30, 0, 6]) 
                    cylinder(h=12, r=10, center=true);
                translate([-30, 0, 6]) 
                    cylinder(h=12, r=10, center=true);
            }

            // Finger grooves
            for(i = [-1, 0, 1]) {
                translate([i * 15, 0, 12]) 
                    cylinder(h=5, r=4, center=true);
            }
        }

        // Rubber grip texture
        color([0.1, 0.1, 0.1]) {
            for(i = [-20:5:20]) {
                translate([i, 0, 12.1]) 
                    cube([3, 18, 0.5], center=true);
            }
        }
    }
}

// Adjustable jaw mechanism
module jaw_assembly() {
    color([0.7, 0.7, 0.7]) {
        // Fixed jaw
        union() {
            // Main jaw body
            translate([-5, 0, 6]) 
                cube([15, 20, 15], center=true);

            // Jaw teeth
            difference() {
                translate([-12, 0, 13]) 
                    cube([8, 20, 6], center=true);

                // Teeth grooves
                for(i = [-8:4:8]) {
                    translate([-12, i, 16]) 
                        cube([8, 1, 6], center=true);
                }
            }
        }

        // Movable jaw
        translate([15, 0, 0]) {
            // Slider base
            difference() {
                cube([25, 18, 12], center=true);

                // Adjustment slot
                translate([0, 0, -1]) 
                    cube([20, 8, 14], center=true);
            }

            // Movable jaw teeth
            difference() {
                translate([10, 0, 13]) 
                    cube([8, 20, 6], center=true);

                for(i = [-8:4:8]) {
                    translate([10, i, 16]) 
                        cube([8, 1, 6], center=true);
                }
            }
        }
    }
}

// Adjustment mechanism
module adjustment_mechanism() {
    color([0.5, 0.5, 0.5]) {
        // Worm screw
        translate([0, 11, 6]) {
            rotate([90, 0, 0]) 
                cylinder(h=25, r=3, center=true);

            // Screw head
            translate([0, 12.5, 0]) 
                cylinder(h=6, r=5, center=true);

            // Knurling on screw head
            difference() {
                cylinder(h=6.1, r=5, center=true);
                for(i = [0:15:360]) {
                    rotate([0, 0, i]) {
                        translate([4, 0, 0]) 
                            cube([2, 1, 6.2], center=true);
                    }
                }
            }
        }

        // Guide rails
        for(side = [-1, 1]) {
            translate([5, side * 9, 6]) 
                cube([20, 2, 10], center=true);
        }
    }
}

// Assembly
union() {
    handle();
    translate([30, 0, 0]) {
        jaw_assembly();
        adjustment_mechanism();
    }

    // Size reference (comment out for print)
    // %cube([85, 30, 12], center=true);
}

// Pedestal Tool - Realistic Style, Medium Complexity
// A small pedestal tool with adjustable height and tool holder

// Overall dimensions
pedestal_height = 80;
pedestal_base_d = 50;
pedestal_top_d = 35;
tool_holder_d = 12;
tool_holder_h = 25;

// Base module
module base() {
    difference() {
        // Main base cylinder
        color("gray") cylinder(h=15, d=pedestal_base_d, $fn=60);

        // Bottom recess for print stability
        translate([0, 0, -1]) 
            cylinder(h=5, d=pedestal_base_d - 6, $fn=60);

        // Ventilation holes
        for(i = [0:3]) {
            rotate([0, 0, i * 90 + 45]) 
                translate([pedestal_base_d/2 - 10, 0, 7.5]) 
                    rotate([0, 90, 0]) 
                        cylinder(h=20, d=4, $fn=20);
        }
    }
}

// Adjustable height column
module column() {
    color("silver") {
        // Main column
        difference() {
            cylinder(h=pedestal_height - 30, d1=pedestal_base_d - 10, d2=pedestal_top_d, $fn=50);

            // Height adjustment slots
            for(i = [1:4]) {
                translate([0, 0, i * 15]) 
                    rotate([0, 0, 0]) 
                        translate([pedestal_base_d/2 - 5 - i*2, 0, 0]) 
                            cube([3, pedestal_base_d - 10 - i*4, 5], center=true);
            }
        }

        // Adjustment mechanism
        translate([0, pedestal_top_d/2 + 5, pedestal_height - 35]) {
            rotate([90, 0, 0]) {
                cylinder(h=15, d=8, $fn=20);
                translate([0, 0, 15]) sphere(d=8);
            }
        }
    }
}

// Tool holder top
module tool_holder() {
    color("darkgray") {
        difference() {
            // Main holder
            translate([0, 0, pedestal_height - 15]) 
                cylinder(h=15, d=pedestal_top_d, $fn=40);

            // Central hole for tools
            translate([0, 0, pedestal_height - 15]) 
                cylinder(h=20, d=tool_holder_d, $fn=30);

            // Tool slots
            for(i = [0:3]) {
                rotate([0, 0, i * 90]) 
                    translate([pedestal_top_d/2 - 5, 0, pedestal_height - 7.5]) 
                        cube([1, 8, 15], center=true);
            }
        }

        // Grip ring
        translate([0, 0, pedestal_height - 20]) {
            color("black") cylinder(h=3, d=pedestal_top_d + 2, $fn=40);
        }
    }
}

// Rubber feet
module feet() {
    for(i = [0:3]) {
        rotate([0, 0, i * 90 + 45]) {
            translate([pedestal_base_d/2 - 8, 0, 0]) {
                color("black") {
                    translate([0, 0, -1]) sphere(d=5);
                    cylinder(h=3, d1=5, d2=3);
                }
            }
        }
    }
}

// Assembly
union() {
    base();
    column();
    tool_holder();
    feet();
}

