// Foot Tool - Realistic Style, Medium Complexity
// A realistic representation of a foot callus remover tool

// Overall dimensions
foot_length = 85;
foot_width = 35;
foot_height = 20;

// Colors
metal_color = [0.7, 0.7, 0.75];
rubber_color = [0.1, 0.1, 0.1];
blade_color = [0.8, 0.8, 0.85];

// Main handle with ergonomic grip
module handle() {
    // Central handle core
    color(rubber_color)
    difference() {
        union() {
            // Main handle body
            hull() {
                translate([-5, -12, 0])
                    cylinder(h=foot_height, r=6, $fn=20);
                translate([5, -12, 0])
                    cylinder(h=foot_height, r=6, $fn=20);
                translate([-5, 12, 0])
                    cylinder(h=foot_height, r=7, $fn=20);
                translate([5, 12, 0])
                    cylinder(h=foot_height, r=7, $fn=20);
            }

            // Grip texture rings
            for(i = [1:5]) {
                translate([0, -10 + i*5, foot_height/2])
                    rotate([90, 0, 0])
                    cylinder(h=25, r1=7.5, r2=7.5, $fn=30);
            }
        }

        // Grip grooves
        for(i = [1:4]) {
            translate([0, -7.5 + i*5, foot_height/2])
                rotate([90, 0, 0])
                cylinder(h=26, r1=6.8, r2=6.8, $fn=30);
        }
    }
}

// Metal blade holder
module blade_holder() {
    color(metal_color)
    union() {
        // Main holder body
        translate([-foot_width/2 + 5, -foot_length/2 + 15, 0])
            cube([foot_width - 10, 20, foot_height - 5]);

        // Rounded end
        translate([0, -foot_length/2 + 15, 0])
            cylinder(h=foot_height - 5, r=foot_width/2 - 5, $fn=30);

        // Blade mounting area
        translate([0, -foot_length/2 + 25, foot_height - 2])
            cube([foot_width - 15, 10, 2], center=true);
    }
}

// Micro-plane blade
module blade() {
    color(blade_color)
    difference() {
        // Blade base
        translate([0, -foot_length/2 + 25, foot_height - 1])
            cube([foot_width - 18, 8, 1], center=true);

        // Cutting holes pattern
        for(y = [-3:3]) {
            for(x = [-8:8]) {
                if((x + y) % 2 == 0) {
                    translate([x*2, y*2 + 25 - foot_length/2, foot_height - 0.5])
                        cylinder(h=2, r=0.4, $fn=8);
                }
            }
        }
    }
}

// Safety guard
module safety_guard() {
    color(metal_color)
    difference() {
        // Main guard
        translate([0, -foot_length/2 + 10, foot_height/2])
            rotate([0, 0, 0])
            cylinder(h=foot_height + 2, r1=foot_width/2 - 3, r2=foot_width/2 - 6, $fn=30);

        // Opening for blade
        translate([0, -foot_length/2 + 25, foot_height/2])
            cube([foot_width - 16, 12, foot_height + 4], center=true);
    }
}

// Hanging loop
module hanging_loop() {
    color(metal_color)
    translate([0, foot_length/2 - 5, foot_height - 5])
        rotate([90, 0, 0])
        difference() {
            cylinder(h=5, r=4, $fn=20);
            translate([0, 0, -1])
                cylinder(h=7, r=2, $fn=20);
        }
}

// Assembly
union() {
    handle();
    blade_holder();
    blade();
    safety_guard();
    hanging_loop();
}

// Leg Tool - Realistic Style, Medium Complexity
// A realistic leg tool with grip, shaft, and foot pad

// Tool dimensions (mm)
handle_length = 35;
handle_diameter = 12;
shaft_length = 65;
shaft_diameter = 6;
foot_width = 25;
foot_length = 45;
foot_thickness = 8;

// Colors
handle_color = "SaddleBrown";
shaft_color = "Silver";
foot_color = "DarkGray";

// Handle grip texture module
module grip_texture(diameter, length, rings) {
    for(i = [0:rings-1]) {
        translate([0, 0, i * (length/rings)])
        difference() {
            cylinder(h=2.5, d=diameter+2, $fn=32);
            cylinder(h=2.7, d=diameter-1, $fn=32);
        }
    }
}

// Handle with grip pattern
module handle() {
    color(handle_color) {
        // Main handle body
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Grip rings
        grip_texture(handle_diameter, handle_length, 8);

        // End cap
        translate([0, 0, handle_length])
        cylinder(h=3, d=handle_diameter+2, $fn=32);
    }
}

// Shaft with slight taper
module shaft() {
    color(shaft_color) {
        translate([0, 0, handle_length])
        cylinder(h=shaft_length, d1=shaft_diameter+1, d2=shaft_diameter, $fn=16);
    }
}

// Foot pad with realistic features
module foot_pad() {
    color(foot_color) {
        // Main foot pad
        translate([0, 0, handle_length+shaft_length])
        rotate([90, 0, 0])
        difference() {
            // Outer shape
            union() {
                // Main pad
                cube([foot_length, foot_width, foot_thickness], center=true);

                // Rounded edges
                translate([foot_length/2-2, 0, 0])
                cylinder(h=foot_width, d=foot_thickness, center=true);
                translate([-foot_length/2+2, 0, 0])
                cylinder(h=foot_width, d=foot_thickness, center=true);
            }

            // Grip pattern on bottom
            for(i = [-2:2]) {
                for(j = [-1:1]) {
                    translate([i*5, j*5, -foot_thickness/2-0.1])
                    cube([3, 3, 1], center=true);
                }
            }

            // Center hole for shaft
            rotate([0, 90, 0])
            cylinder(h=foot_length+2, d=shaft_diameter+2, center=true);
        }
    }
}

// Assembly
union() {
    handle();
    shaft();
    foot_pad();

    // Reinforcement ring
    color(shaft_color) {
        translate([0, 0, handle_length+shaft_length-foot_thickness/2])
        rotate([90, 0, 0])
        cylinder(h=foot_thickness+2, d=shaft_diameter+4, center=true);
    }
}

