// OpenSCAD Column Tool - Realistic Medium Complexity
// Column marking tool for metalworking / woodworking

// Overall dimensions
column_height = 85;
column_diameter = 22;
base_diameter = 28;
handle_length = 40;
handle_diameter = 12;

// Create the main column body with knurled grip
module column_body() {
    color("silver")
    difference() {
        // Main cylindrical body
        cylinder(h=column_height, d=column_diameter, $fn=64);

        // Knurling pattern - cut shallow grooves
        for(i=[0:35]) {
            rotate([0, 0, i*10])
            translate([-column_diameter/2-0.1, -1, 5])
            cube([column_diameter+0.2, 2, column_height-10]);
        }
    }
}

// Create the pointed scribe tip
module scribe_tip() {
    color("silver")
    union() {
        // Conical tip
        cylinder(h=8, d1=column_diameter, d2=2, $fn=32);

        // Sharp point
        cylinder(h=4, d1=2, d2=0.5, $fn=16);
    }
}

// Create the measurement scale markings
module scale_markings() {
    color("black")
    for(i=[0:10]) {
        height = i * 7;
        if(i % 5 == 0) {
            // Major marks
            translate([column_diameter/2+0.1, -2, height])
            cube([3, 4, 0.5]);
        } else {
            // Minor marks
            translate([column_diameter/2+0.1, -1, height])
            cube([2, 2, 0.5]);
        }
    }
}

// Create the ergonomic handle
module handle() {
    color("darkslategray")
    difference() {
        union() {
            // Main handle cylinder
            translate([0, 0, column_height])
            cylinder(h=handle_length, d=handle_diameter, $fn=32);

            // Handle grip rings
            for(i=[0:3]) {
                translate([0, 0, column_height + 5 + i*10])
                cylinder(h=2, d=handle_diameter+2, $fn=32);
            }
        }

        // Finger grooves
        for(i=[0:2]) {
            rotate([0, 0, 45 + i*45])
            translate([handle_diameter/2-1, -2, column_height+5])
            cube([4, 4, handle_length-10]);
        }
    }
}

// Create the base collar
module base_collar() {
    color("silver")
    difference() {
        // Main base
        cylinder(h=6, d=base_diameter, $fn=64);

        // Chamfer
        translate([0, 0, -0.1])
        cylinder(h=6.2, d1=base_diameter-2, d2=base_diameter, $fn=64);
    }
}

// Create the depth adjustment mechanism
module depth_adjuster() {
    color("gold")
    union() {
        // Adjustment knob
        translate([0, 0, -12])
        cylinder(h=8, d=15, $fn=32);

        // Knurling on knob
        for(i=[0:15]) {
            rotate([0, 0, i*22.5])
            translate([-7.5-0.1, -0.5, -12])
            cube([15+0.2, 1, 8]);
        }

        // Threaded section
        translate([0, 0, -20])
        cylinder(h=8, d=10, $fn=16);
    }
}

// Assembly
union() {
    // Base at bottom
    translate([0, 0, 0])
    base_collar();

    // Main column body
    translate([0, 0, 6])
    column_body();

    // Scale markings on column
    scale_markings();

    // Handle on top
    handle();

    // Scribe tip at bottom
    translate([0, 0, -20])
    depth_adjuster();

    // Scribe tip extends through base
    translate([0, 0, 6])
    scribe_tip();
}

// Pier Tool - Realistic Style, Medium Complexity
// A handheld tool used by mechanics for removing and installing pier pins

$fn = 50;

// Main colors
color_tool = "#4A4A4A";
color_handle = "#2E2E2E";
color_grip = "#FF4444";
color_metal = "#C0C0C0";

// Overall dimensions
length = 85;
width = 20;
height = 15;

module handle() {
    // Main handle body
    color(color_handle)
    difference() {
        union() {
            // Handle shaft
            translate([0, 0, 0])
                cylinder(h=60, d=12, center=true);

            // End cap
            translate([30, 0, 0])
                sphere(d=13);
        }

        // Hollow center
        translate([0, 0, 0])
            cylinder(h=70, d=6, center=true);
    }

    // Grip rings
    for(i = [-25:7:25]) {
        color(color_grip)
        translate([i, 0, 0])
            cylinder(h=5, d=14.5);
    }
}

module working_head() {
    // Main head body
    color(color_tool)
    union() {
        // Cylindrical neck
        translate([-32, 0, 0])
            cylinder(h=15, d=10);

        // Forked end
        translate([-40, 0, 0])
            rotate([0, 90, 0])
            linear_extrude(height=4)
            polygon(points=[
                [0, -8],
                [15, -8],
                [15, -3],
                [20, 0],
                [15, 3],
                [15, 8],
                [0, 8]
            ]);

        // Reinforcement ridge
        translate([-39, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=6, d=12);
    }

    // Pier pin slot
    color("black")
    translate([-42, 0, 0])
        rotate([0, 90, 0])
        cube([8, 3, 4], center=true);
}

module pivot_pin() {
    // Pivot pin mechanism
    color(color_metal)
    union() {
        // Pin head
        translate([-20, 0, 0])
            cylinder(h=3, d=6);

        // Pin shaft
        translate([-20, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=width+2, d=4);
    }
}

module text_marks() {
    // Tool markings
    color("white")
    translate([0, 0, 7.5])
        linear_extrude(height=0.3)
        text("PIER TOOL", size=3, halign="center", valign="center");
}

// Assembly
union() {
    handle();
    translate([30, 0, 0])
        working_head();
    pivot_pin();
    text_marks();
}

// OpenSCAD Abutment Tool – medium-complexity, realistic style
// Overall footprint ≈ 60 mm long, 25 mm tall, 20 mm wide – 3-D-print friendly

$fn = 64;                       // Smooth curves

// --- Color constants ---
c_body   = "#B0B0B0";          // anodized aluminium look
c_grip   = "#202020";          // rubberized handle
c_mark   = "#FF0000";          // red depth marks

// --- Main assembly ---
module abutment_assembly() {
    union() {
        base_block();          // main rectangular body
        grip_handle();         // ergonomic handle
        reference_lip();       // vertical registration edge
        depth_marks();         // laser-etched style markings
        drill_guide_holes();   // two bushings
    }
}

// --- Rectangular base body ---
module base_block() {
    color(c_body)
    difference() {
        // Main block
        translate([0, 0, 5])
            cube([60, 20, 10], center = true);

        // Central light-weighting pocket (top)
        translate([0, 0, 8])
            cube([40, 14, 4], center = true);

        // Chamfers on long edges (bottom)
        for (x = [-25, 25])
            translate([x, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h = 20, r = 2, center = true);
    }
}

// --- Rubberized grip handle ---
module grip_handle() {
    color(c_grip)
    difference() {
        // Main curved handle
        translate([-30, 0, 0])
            rotate([0, 90, 0])
                scale([1, 1, 0.8])
                    cylinder(h = 30, r = 8, center = true);

        // Finger grooves (3 evenly spaced)
        for (i = [-1:1])
            translate([-30, i*7, 0])
                rotate([90, 0, 0])
                    cylinder(h = 30, r = 3, center = true);
    }
}

// --- Vertical reference lip ---
module reference_lip() {
    color(c_body)
    translate([25, 0, 10])
        cube([2, 20, 15], center = true);
}

// --- Red depth graduation marks ---
module depth_marks() {
    color(c_mark)
    for (d = [5:5:20]) {
        translate([0, 11, d])
            cube([60, 0.5, 0.5], center = true);
    }
}

// --- Drill-guide bushings (press-fit brass tube placeholders) ---
module drill_guide_holes() {
    spacing = 15;
    for (y = [-spacing/2, spacing/2]) {
        color(c_body)
        translate([0, y, 10])
            cylinder(h = 20, r = 3.5, center = true);
        // Actual hole (subtract)
        translate([0, y, 10])
            cylinder(h = 22, r = 2, center = true);
    }
}

// --- Render the complete tool ---
abutment_assembly();

// Buttress Tool - Realistic Style, Medium Complexity
// Overall dimensions: ~80mm tall x 60mm wide x 20mm thick

// Colors
$fn = 32;
handle_color = "DimGray";
shaft_color = "Silver";
head_color = "DarkSlateGray";
grip_color = "Black";
marking_color = "Red";

// Main module
module buttress_tool() {
    union() {
        // Handle assembly
        translate([0, 0, 0]) handle_assembly();

        // Shaft
        translate([0, 0, 35]) shaft();

        // Buttress head
        translate([0, 0, 65]) buttress_head();
    }
}

// Handle with ergonomic grip
module handle_assembly() {
    color(handle_color) {
        // Main handle body
        cylinder(h = 30, d = 20, $fn = 32);

        // Ergonomic grip rings
        for(i = [5:5:25]) {
            translate([0, 0, i]) color(grip_color) 
                cylinder(h = 2, d1 = 22, d2 = 20);
        }

        // Base cap
        translate([0, 0, 0]) 
            cylinder(h = 3, d = 22);
    }
}

// Central shaft
module shaft() {
    color(shaft_color) {
        // Main shaft
        cylinder(h = 25, d = 10);

        // Reinforcement ring
        translate([0, 0, 0]) 
            cylinder(h = 3, d = 12);

        // Model identification marking
        translate([6, 0, 12]) rotate([0, 90, 0]) 
            color(marking_color) 
            linear_extrude(height = 0.5) 
            text("8mm", size = 3);
    }
}

// Buttress thread head
module buttress_head() {
    color(head_color) {
        // Main head body
        cylinder(h = 12, d = 18);

        // Buttress thread cutting teeth
        for(i = [0:30:330]) {
            rotate([0, 0, i]) 
                translate([8, 0, 0]) 
                cutting_tooth();
        }

        // Central guide hole
        translate([0, 0, -1]) 
            cylinder(h = 14, d = 6);

        // Top chamfer
        translate([0, 0, 12]) 
            cylinder(h = 2, d1 = 18, d2 = 14);
    }
}

// Individual cutting tooth
module cutting_tooth() {
    difference() {
        // Main tooth body
        translate([0, 0, 6]) 
            cube([6, 4, 8], center = true);

        // Cutting edge angle
        translate([3, 0, 2]) 
            rotate([0, 0, -15]) 
            cube([6, 4.2, 8], center = true);

        // Relief angle
        translate([1.5, 0, 6]) 
            rotate([0, 0, 45]) 
            cube([6, 4.2, 4], center = true);
    }
}

// Render the tool
buttress_tool();

// Arch tool for drawing smooth curved shapes
// 3D printable version with ergonomic grip and replaceable blade

// Main parameters
blade_length = 30;
blade_width = 2;
blade_thickness = 0.5;
handle_length = 60;
handle_diameter = 12;
arch_radius = 25;
arch_thickness = 3;

// Colors
handle_color = "BurlyWood";
blade_color = "Silver";
accent_color = "DimGray";

// Main assembly
union() {
    // Handle assembly
    color(handle_color) handle_assembly();

    // Blade assembly
    color(blade_color) blade_assembly();

    // Accent rings
    color(accent_color) accent_details();
}

// Handle with grip texture
module handle_assembly() {
    difference() {
        union() {
            // Main handle cylinder
            cylinder(h=handle_length, d=handle_diameter, center=false);

            // End cap
            translate([0, 0, handle_length])
            cylinder(h=5, d=handle_diameter+2, center=false);

            // Grip rings
            for(i = [5:10:handle_length-15]) {
                translate([0, 0, i])
                cylinder(h=2, d=handle_diameter+1, center=false);
            }
        }

        // Center hole for weight reduction
        cylinder(h=handle_length+5, d=handle_diameter/2, center=false);
    }
}

// Curved blade assembly
module blade_assembly() {
    // Rotate to align with handle
    translate([0, 0, handle_length-5])
    rotate([90, 0, 0])
    union() {
        // Main arch blade
        difference() {
            // Outer arch
            intersection() {
                // Full arch
                rotate_extrude(angle=180, $fn=50)
                translate([arch_radius, 0, 0])
                square([blade_thickness, blade_length]);

                // Taper the ends
                translate([0, 0, -blade_length/2])
                cube([arch_radius*2+blade_thickness, blade_length*2, blade_length], center=true);
            }

            // Inner arch for blade edge
            rotate_extrude(angle=180, $fn=50)
            translate([arch_radius-blade_width, 0, 0])
            square([blade_thickness+0.5, blade_length+1]);
        }

        // Blade support ribs
        for(angle = [30:30:150]) {
            rotate([0, 0, angle])
            translate([arch_radius, 0, -blade_length/2])
            cube([blade_thickness*2, blade_length, blade_thickness], center=true);
        }
    }
}

// Accent details and markings
module accent_details() {
    // Ring at handle base
    translate([0, 0, handle_length-10])
    difference() {
        cylinder(h=3, d=handle_diameter+2, center=true);
        cylinder(h=4, d=handle_diameter, center=true);
    }

    // Measurement marks on handle
    for(i = [10:10:handle_length-20]) {
        translate([handle_diameter/2, 0, i])
        cube([1, 0.5, 0.5], center=true);
    }

    // Branding plate
    translate([handle_diameter/2+0.5, 0, handle_length/2])
    cube([2, 8, 4], center=true);
}
