// Medium-complexity 3D-printable sedan
// Overall scale: 90 mm long, 40 mm wide, 30 mm tall
scale_factor = 1;

// Body shell
module body_shell() {
    difference() {
        // Main body block
        hull() {
            translate([-42, -18, 0]) cube([84, 36, 20]);
            translate([-40, -16, 15]) cube([80, 32, 10]);
            translate([35, -12, 22]) cube([10, 24, 8]);
            translate([-45, -12, 22]) cube([10, 24, 8]);
        }
        // Cabin cutout
        translate([-35, -14, 18]) cube([70, 28, 15]);
        // Wheel arches
        for (x = [-28, 28]) {
            translate([x, -20, 0]) rotate([0, 0, 0]) cylinder(h = 20, r = 8, $fn = 16);
            translate([x,  20, 0]) rotate([0, 0, 0]) cylinder(h = 20, r = 8, $fn = 16);
        }
    }
}

// Hood and trunk
module hood_trunk() {
    // Hood
    translate([-41, -17, 10]) cube([30, 34, 8]);
    // Trunk
    translate([11, -17, 10]) cube([30, 34, 8]);
}

// Roof
module roof() {
    translate([-30, -14, 25]) cube([60, 28, 6]);
}

// Windows
module windows() {
    color("lightblue") {
        // Windshield
        translate([-34, -13, 19]) cube([0.5, 26, 12]);
        // Rear window
        translate([33.5, -13, 19]) cube([0.5, 26, 12]);
        // Side windows (front)
        for (x = [-20, 20]) {
            translate([x, -14.5, 19]) cube([14, 0.5, 10]);
            translate([x,  14, 19]) cube([14, 0.5, 10]);
        }
    }
}

// Wheels
module wheels() {
    for (x = [-28, 28]) {
        for (y = [-18, 18]) {
            translate([x, y, -2]) {
                difference() {
                    cylinder(h = 6, r = 8, $fn = 32);
                    cylinder(h = 7, r = 4, $fn = 32);
                }
            }
        }
    }
}

// Headlights
module headlights() {
    for (x = [-38, -30]) {
        translate([x, -15, 12]) cube([4, 2, 2]);
        translate([x, 13, 12]) cube([4, 2, 2]);
    }
}

// Taillights
module taillights() {
    for (x = [30, 38]) {
        translate([x, -15, 12]) cube([4, 2, 2]);
        translate([x, 13, 12]) cube([4, 2, 2]);
    }
}

// Side mirrors
module mirrors() {
    for (y = [-15, 15]) {
        translate([-10, y, 22]) {
            cube([3, 2, 1.5]);
            translate([1.5, 0, 0]) rotate([0, y > 0 ? 30 : -30, 0]) cube([2, 1.5, 3]);
        }
    }
}

// License plates
module plates() {
    translate([-43, -6, 8]) cube([2, 12, 1]);
    translate([41, -6, 8]) cube([2, 12, 1]);
}

// Exhaust pipe
module exhaust() {
    translate([42, 8, 4]) rotate([0, 90, 0]) cylinder(h = 10, r = 2, $fn = 16);
}

// Assemble the sedan
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        body_shell();
        hood_trunk();
        roof();
        windows();
        headlights();
        taillights();
        mirrors();
        plates();
        exhaust();
    }
    wheels();
}

// Realistic Medium-complexity 3D-printable coupe (≈85 mm long)

$fn = 36;               // Smooth curves for printing

wheel_d = 18;
wheel_w = 8;
body_l = 85;
body_w = 40;
body_h = 22;

// Printable base plate
module base_plate() {
    difference() {
        translate([0,0,-1])
            cube([body_l+4,body_w+4,2], center=true);
        translate([0,0,0.5])
            cube([body_l,body_w,2], center=true);
    }
}

// Wheel module (no overhangs)
module wheel() {
    rotate([0,90,0])
    difference() {
        cylinder(d=wheel_d, h=wheel_w, center=true);
        translate([0,0,-wheel_w/2-0.5])
            cylinder(d=wheel_d-6, h=wheel_w+1, center=true);
    }
}

// Side mirrors (printable)
module mirror() {
    translate([0,-6,0])
    rotate([0,20,0])
    union() {
        cube([3,2,3]);
        translate([1.5,0,-1])
            sphere(d=4);
    }
}

// Main body
module body() {
    difference() {
        // Basic hull
        hull() {
            translate([-body_l/2+8, 0, body_h/2])
                cube([body_l-16, body_w-8, body_h], center=true);
            translate([-body_l/2+15, 0, body_h/2+8])
                cube([body_l-30, body_w-12, 8], center=true);
        }

        // Cabin cavity
        translate([-body_l/2+25, 0, body_h/2+10])
            cube([body_l-50, body_w-20, 12], center=true);

        // Windows
        translate([-body_l/2+30, 0, body_h/2+11])
            cube([body_l-60, body_w-16, 8], center=true);
    }
}

// Hood slope
module hood() {
    translate([-body_l/2+5, 0, body_h/2+2])
        rotate([0,0,0])
        linear_extrude(height=10, scale=0.5)
        square([25, body_w-10], center=true);
}

// Lights
module lights() {
    for(x=[-1,1])
        translate([x*body_l/2-4, 0, body_h/2])
            sphere(d=6);
}

// Exhausts
module exhaust() {
    for(side=[-1,1])
        translate([-body_l/2+3, side*(body_w/2-2), -2])
            rotate([90,0,0])
            cylinder(d1=4, d2=3, h=8);
}

// Roof
module roof() {
    translate([-5,0,body_h/2+16])
        rotate([0,0,0])
        hull() {
            cube([30, body_w-16, 2], center=true);
            translate([5,0,8])
                cube([20, body_w-20, 2], center=true);
        }
}

// Assemble the coupe
union() {
    base_plate();

    // Wheels
    for(x=[-1,1])
        for(y=[-1,1])
            translate([x*28, y*22, 0])
                wheel();

    body();
    roof();
    hood();
    lights();
    exhaust();

    // Side mirrors
    translate([body_l/2-20, body_w/2+1, body_h/2+8])
        mirror();
    translate([body_l/2-20, -body_w/2-1, body_h/2+8])
        mirror();
}

// Realistic Medium-Complexity 3D-Printable Hatchback
// Approx 70 mm long, 30 mm wide, 25 mm tall
// Wall thickness 1.2 mm for reliable printing

$fa = 2; $fs = 0.5;

module body_shell() {
    // Main body with slight wedge shape
    hull() {
        translate([0, 0, 5]) cube([40, 28, 10], center = true);
        translate([8, 0, 8]) cube([42, 26, 12], center = true);
    }
}

module roof() {
    // Sloping roof
    translate([3, 0, 14.5])
    rotate([0, -4, 0])
    cube([38, 26, 2.4], center = true);
}

module hood() {
    // Curved hood by stacking cylinders
    for (i = [-10:2:10])
        translate([i, 0, 12 + 0.015 * i * i])
        rotate([90, 0, 0])
        cylinder(h = 1.2, r = 13 - 0.3 * abs(i), center = true);
}

module hatch() {
    // Rear hatch with slight rake
    translate([-17, 0, 10.5])
    rotate([60, 0, 0])
    cube([17, 25, 1.2], center = true);
}

module side_windows() {
    // Front and rear side windows
    translate([5, 14.1, 12])
    cube([12, 0.1, 7], center = true);
    translate([-9, 14.1, 11])
    cube([14, 0.1, 6], center = true);
}

module windshield() {
    // Front windshield
    translate([13, 0, 13])
    rotate([70, 0, 0])
    cube([20, 27, 0.1], center = true);
}

module rear_windshield() {
    // Rear windshield
    translate([-14, 0, 12])
    rotate([110, 0, 0])
    cube([18, 25, 0.1], center = true);
}

module wheel(pos, angle = 0) {
    // Wheel with tire and rim
    translate(pos)
    rotate([0, 0, angle])
    difference() {
        // Tire
        cylinder(h = 4.8, r = 6, center = true);
        // Rim cavity
        translate([0, 0, -0.1])
        cylinder(h = 5, r = 4, center = true);
    }
}

module axles() {
    // Simple axles connecting wheels
    translate([10, 0, 3])
    rotate([0, 90, 0])
    cylinder(h = 34, r = 1.2, center = true);
    translate([-10, 0, 3])
    rotate([0, 90, 0])
    cylinder(h = 34, r = 1.2, center = true);
}

module bumper(loc) {
    // Front or rear bumper
    translate(loc)
    cube([1.2, 28, 4], center = true);
}

module headlights() {
    // Headlight clusters
    translate([20.5, 8, 8])
    cube([1.2, 6, 4], center = true);
    translate([20.5, -8, 8])
    cube([1.2, 6, 4], center = true);
}

module taillights() {
    // Taillight clusters
    translate([-20.5, 8, 8])
    cube([1.2, 5, 3], center = true);
    translate([-20.5, -8, 8])
    cube([1.2, 5, 3], center = true);
}

module exhaust_pipe() {
    // Single exhaust tip
    translate([-21, 10, 2])
    rotate([90, 0, 0])
    cylinder(h = 6, r = 1.2, center = true);
}

// Assembly
union() {
    difference() {
        // Main body union
        union() {
            body_shell();
            roof();
            hood();
            hatch();
            windshield();
            rear_windshield();
            side_windows();
            bumper([21, 0, 2]);
            bumper([-21, 0, 2]);
        }
        // Hollow interior
        translate([0, 0, 5])
        scale([0.8, 0.8, 0.8])
        cube([45, 30, 18], center = true);
    }

    // External parts
    wheel([10, 15, 3]);
    wheel([10, -15, 3]);
    wheel([-10, 15, 3]);
    wheel([-10, -15, 3]);
    axles();
    headlights();
    taillights();
    exhaust_pipe();
}

// Realistic SUV - Medium Complexity
// Designed for 3D printing (no overhangs, 2mm wall thickness)

module suv_body() {
    // Main body
    difference() {
        // Main hull
        hull() {
            translate([-15, -20, 0]) cube([30, 40, 30]);
            translate([-10, -18, 30]) cube([20, 36, 25]);
        }

        // Wheel wells
        for(side = [-1, 1]) {
            translate([side * 18, -15, 5]) rotate([0, 90, 0]) 
                cylinder(h=15, r=9, $fn=32);
            translate([side * 18, 15, 5]) rotate([0, 90, 0]) 
                cylinder(h=15, r=9, $fn=32);
        }
    }
}

module suv_windows() {
    // Windshield
    translate([0, -16, 33]) rotate([30, 0, 0])
        cube([14, 1, 15], center=true);

    // Rear window
    translate([0, 16, 33]) rotate([-30, 0, 0])
        cube([14, 1, 15], center=true);

    // Side windows
    for(side = [-1, 1]) {
        translate([side * 12, 0, 35]) rotate([0, 0, 30 * side])
            cube([20, 1, 12], center=true);
    }
}

module suv_wheels() {
    module wheel() {
        difference() {
            // Tire
            cylinder(h=8, r=9, $fn=32);
            // Rim
            translate([0, 0, -1]) cylinder(h=10, r=7, $fn=32);
            // Spokes
            for(i = [0:3]) {
                rotate([0, 0, i * 90])
                    translate([0, 5, 4]) cube([1, 2, 8], center=true);
            }
        }
    }

    // Front wheels
    translate([-18, -15, 0]) wheel();
    translate([18, -15, 0]) mirror([1, 0, 0]) wheel();

    // Rear wheels
    translate([-18, 15, 0]) wheel();
    translate([18, 15, 0]) mirror([1, 0, 0]) wheel();
}

module suv_details() {
    // Front grille
    translate([0, -19.5, 20]) cube([12, 1, 8], center=true);

    // Headlights
    for(side = [-1, 1]) {
        translate([side * 10, -19.5, 22]) 
            cube([3, 1, 3], center=true);
    }

    // Taillights
    for(side = [-1, 1]) {
        translate([side * 10, 19.5, 22]) 
            cube([3, 1, 3], center=true);
    }

    // Roof rails
    for(side = [-1, 1]) {
        hull() {
            translate([side * 12, -15, 55]) 
                cylinder(h=2, r=1, $fn=16);
            translate([side * 12, 15, 55]) 
                cylinder(h=2, r=1, $fn=16);
        }
    }

    // Bumpers
    translate([0, -20, 10]) cube([20, 2, 8], center=true);
    translate([0, 20, 10]) cube([20, 2, 8], center=true);
}

// Assemble the SUV
module suv() {
    union() {
        suv_body();
        suv_windows();
        suv_wheels();
        suv_details();
    }
}

// Scale to 80mm length
scale(0.8) suv();

// Realistic medium-complexity 3D-printable truck (approx. 85 mm long)

$fn = 36; // Smoother curves for realism
wall = 2;  // 3D-printable wall thickness

module wheel(r=9, w=6) {
    difference() {
        cylinder(h=w, r=r, center=true);
        cylinder(h=w+1, r=r-wall, center=true); // Hollow tire
    }
}

module axle() {
    cylinder(h=30, r=2, center=true);
}

module chassis() {
    // Main frame
    translate([0, 0, 8])
    difference() {
        cube([80, 30, 16], center=true);
        translate([0, 0, -1])
        cube([76, 26, 14], center=true); // Hollow out
    }

    // Front bumper
    translate([39, 0, 8])
    cube([4, 28, 12], center=true);

    // Rear bumper
    translate([-39, 0, 8])
    cube([4, 28, 12], center=true);
}

module cab() {
    translate([20, 0, 22])
    difference() {
        cube([30, 28, 28], center=true);
        translate([0, 0, -1])
        cube([26, 24, 26], center=true); // Hollow cab
    }

    // Windshield slope
    translate([30, 0, 36])
    rotate([0, 30, 0])
    cube([20, 26, 2], center=true);

    // Roof
    translate([20, 0, 40])
    cube([28, 24, 4], center=true);

    // Side mirrors
    for(side=[-1,1]) {
        translate([32, side*15, 28])
        cube([4, 2, 6], center=true);
        translate([34, side*18, 28])
        sphere(r=2);
    }
}

module bed() {
    translate([-25, 0, 20])
    difference() {
        cube([50, 32, 12], center=true);
        translate([0, 0, -1])
        cube([46, 28, 10], center=true); // Hollow bed
    }

    // Bed sides
    translate([-25, 16, 28])
    cube([50, 2, 8], center=true);
    translate([-25, -16, 28])
    cube([50, 2, 8], center=true);

    // Tailgate (vertical)
    translate([-49, 0, 26])
    cube([2, 28, 20], center=true);
}

module headlights() {
    for(side=[-1,1]) {
        translate([40, side*10, 16])
        sphere(r=3);
        translate([40, side*10, 16])
        sphere(r=2.5); // Inner lens
    }
}

module exhaust() {
    translate([-10, 18, 24])
    cylinder(h=30, r=2);
    translate([-10, 18, 54])
    sphere(r=3);
}

// Assemble truck
union() {
    chassis();
    cab();
    bed();
    headlights();
    exhaust();

    // Wheels and axles
    for(side=[-1,1]) {
        // Front axle
        translate([20, side*18, 8])
        wheel();
        translate([20, side*18, 8])
        rotate([90,0,0])
        axle();

        // Rear axle
        translate([-20, side*18, 8])
        wheel();
        translate([-20, side*18, 8])
        rotate([90,0,0])
        axle();
    }
}

// Medium-complexity realistic van
// Overall size ~80 mm (L) × 30 mm (W) × 30 mm (H)

$fn = 36;

module wheel(r=5, w=3) {
    // Tire + rim
    difference() {
        cylinder(h=w, r=r);          // tire
        cylinder(h=w+0.1, r=r-1.5);  // rim cavity
    }
}

module wheels() {
    // Front left
    translate([-25, 15, -5]) wheel();
    // Front right
    translate([-25, -15, -5]) wheel();
    // Rear left
    translate([25, 15, -5]) wheel();
    // Rear right
    translate([25, -15, -5]) wheel();
}

module body() {
    // Main lower box
    hull() {
        translate([-30, 0, 0])
            cube([60, 30, 15], center=true);
        translate([-30, 0, 10])
            cube([60, 30, 10], center=true);
    }

    // Cabin roof
    hull() {
        translate([-20, 0, 12])
            cube([25, 28, 8], center=true);
        translate([-5, 0, 15])
            cube([20, 26, 6], center=true);
    }
}

module windows() {
    color("lightblue") {
        // Windshield
        translate([-28, 0, 13])
            cube([0.1, 22, 10], center=true);
        // Side windows
        for(y=[8,-8])
            translate([-15, y, 13])
                cube([20, 0.1, 8], center=true);
    }
}

module doors() {
    // Driver door seam
    translate([-15, 15, 7.5])
        cube([30, 0.2, 15], center=true);
    // Sliding door seam
    translate([5, 15, 7.5])
        cube([20, 0.2, 15], center=true);
}

module headlights() {
    // Headlights
    translate([-30, 8, 5])
        cube([1, 4, 3], center=true);
    translate([-30, -8, 5])
        cube([1, 4, 3], center=true);
    // Taillights
    translate([30, 8, 5])
        cube([1, 4, 3], center=true);
    translate([30, -8, 5])
        cube([1, 4, 3], center=true);
}

module grille() {
    // Front grille
    translate([-30, 0, 3])
        cube([0.5, 20, 6], center=true);
    // Horizontal bars
    for(z=[1:2:5])
        translate([-30.2, 0, z])
            cube([0.1, 18, 0.5], center=true);
}

module mirrors() {
    // Side mirrors
    translate([-25, 16, 10])
        cube([2, 4, 2], center=true);
    translate([-25, -16, 10])
        cube([2, 4, 2], center=true);
}

// Assembly
union() {
    body();
    windows();
    doors();
    headlights();
    grille();
    mirrors();
    wheels();
}
